CREATE TABLE IF NOT EXISTS `food` (
  `id_food` int(11) NOT NULL AUTO_INCREMENT,
  `id_type` int(11) NOT NULL,
  `name_food` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `image_food` varchar(100) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'default.jpg' COMMENT 'có ảnh mặc định',
  `mini_content` varchar(500) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'mổ tả ngắn',
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `unit_price` double NOT NULL,
  `time_add` int(11) DEFAULT NULL,
  `time_edit` int(11) DEFAULT NULL,
  `today` tinyint(1) NOT NULL,
  `author` int(11) DEFAULT NULL,
  `cook` int(11) DEFAULT NULL,
  PRIMARY KEY (`id_food`),
  KEY `name_food` (`name_food`),
  KEY `author` (`author`),
  KEY `cook` (`cook`),
  KEY `id_type` (`id_type`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=25 ;

