INSERT INTO `food` (`id_food`, `id_type`, `name_food`, `image_food`, `mini_content`, `content`, `unit_price`, `time_add`, `time_edit`, `today`, `author`, `cook`) VALUES
(5, 3, 'Banh Khọt', 'banh_khot.jpg', 'Bánh khọt là loại bánh Việt Nam làm từ bột gạo, có nhân tôm, được..', '<p>B&aacute;nh khọt l&agrave; m&oacute;n ăn tương đối phổ biến ở ph&iacute;a Nam v&agrave; c&oacute; nhiều biến thể kh&aacute;c nhau t&ugrave;y từng địa phương. C&ocirc;ng thức l&agrave;m b&aacute;nh của người Cần Thơ như sau:</p>\r\n\r\n<p>Nguy&ecirc;n liệu</p>\r\n\r\n<p>Khu&ocirc;n b&aacute;nh khọt.</p>\r\n\r\n<p>Bột gạo: 1 kg + bột nghệ. C&oacute; thể mua g&oacute;i bột b&aacute;nh x&egrave;o b&aacute;n sẵn - đ&atilde; pha sẵn bột nghệ, nếu muốn b&aacute;nh v&agrave;ng hơn th&igrave; cho th&ecirc;m 1/2 th&igrave;a c&agrave; ph&ecirc; bột nghệ.</p>\r\n\r\n<p>Thịt nạc dăm băm 400 gr, t&ocirc;m tươi 300 gr, trứng vịt 3 quả.</p>\r\n\r\n<p>Dừa nạo 500 gr, đậu xanh hột 300 gr, c&agrave; rốt - củ cải trắng: mỗi loại 1 củ nhỏ.</p>\r\n\r\n<p>H&agrave;nh l&aacute;, h&agrave;nh t&acirc;y, h&agrave;nh t&iacute;m.&nbsp;Ti&ecirc;u, đường, muối, bột ngọt.&nbsp;Chanh, ớt, nước mắm, rau sống (cải xanh, x&agrave; l&aacute;ch, rau sống, rau thơm c&aacute;c loại).</p>\r\n\r\n<p>Sơ chế</p>\r\n\r\n<p>Khu&ocirc;n b&aacute;nh khọt nếu mới mua về th&igrave; n&ecirc;n xử l&yacute; qua bằng c&aacute;ch cho khu&ocirc;n l&ecirc;n bếp để n&oacute;ng, thoa dầu ăn xung quanh khu&ocirc;n li&ecirc;n tục 5-10 ph&uacute;t, nhờ thế khi đổ bột b&aacute;nh th&igrave; bột sẽ kh&ocirc;ng bị d&iacute;nh trong khu&ocirc;n. Sử dụng &iacute;t vải m&ugrave;ng quấn quanh chiếc đũa d&ugrave;ng để thoa dầu ăn mỗi khi đổ b&aacute;nh.</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;"><img alt="DSC01970-JPG-3779-1411115173.jpg" src="http://m.f13.img.vnecdn.net/2014/09/19/DSC01970-JPG-3779-1411115173.jpg" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style="text-align: center;">Xử l&yacute; khu&ocirc;n trước khi l&agrave;m b&aacute;nh.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>500gr dừa nạo&nbsp;vắt lấy nước cốt 500 ml (1/5 cho v&agrave;o hỗn hợp nh&acirc;n, 4/5 c&ograve;n lại cho v&agrave;o bột b&aacute;nh).&nbsp;T&ocirc;m tươi l&agrave;m sạch cắt hạt lựu hoặc khoanh tr&ograve;n.</p>\r\n\r\n<p>C&agrave; rốt v&agrave; củ cải trắng cắt sợi mỏng, b&oacute;p với &iacute;t muối, rửa sạch, vắt cho r&aacute;o.&nbsp;Đậu xanh để vỏ hay bỏ vỏ t&ugrave;y &yacute; th&iacute;ch, đem luộc vừa ch&iacute;n.&nbsp;H&agrave;nh t&acirc;y cắt hạt lưu. H&agrave;nh t&iacute;m th&aacute;i mỏng. H&agrave;nh l&aacute; xắt kh&uacute;c nửa ph&acirc;n.</p>\r\n\r\n<p>Chế biến</p>\r\n\r\n<p>Trộn bột b&aacute;nh x&egrave;o, bột nghệ, h&agrave;nh l&aacute;, trứng vịt lấy cả l&ograve;ng trắng, đỏ v&agrave; nước cốt dừa trong nước ấm. Quậy đều tay cho bột kh&ocirc;ng bị v&oacute;n cục, cho th&ecirc;m ch&uacute;t muối v&agrave;o. Để bột nghỉ khoảng 15-30 ph&uacute;t. Bột b&aacute;nh x&egrave;o khi pha theo c&ocirc;ng thức ghi tr&ecirc;n bao b&igrave;, bạn c&oacute; thể bớt lại một ch&uacute;t nước, v&igrave; trong l&uacute;c pha bột sẽ c&ograve;n c&oacute; th&ecirc;m nước cốt dừa. Ngo&agrave;i ra, để b&aacute;nh c&oacute; độ gi&ograve;n th&igrave; n&ecirc;n cho 1-2 vi&ecirc;n đ&aacute; v&agrave;o, khi đổ b&aacute;nh sẽ gi&ograve;n.</p>\r\n\r\n<p>Bắc chảo l&ecirc;n bếp cho dầu ăn v&agrave;o đun n&oacute;ng, phi h&agrave;nh thơm sau đ&oacute; cho thịt nạc dăm, t&ocirc;m tươi, đậu xanh luộc vừa ch&iacute;n, h&agrave;nh t&acirc;y v&agrave;o x&agrave;o chung sơ qua, n&ecirc;m nếm vừa ăn, cho ra t&ocirc;, sau đ&oacute; cho 1/5 nước cốt dừa c&ograve;n lại v&agrave;o trộn chung thật đều. Hỗn hợp nh&acirc;n đ&atilde; xong.</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;"><img alt="DSC01971-JPG-1149-1411115173.jpg" src="http://m.f13.img.vnecdn.net/2014/09/19/DSC01971-JPG-1149-1411115173.jpg" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style="text-align: center;">X&agrave;o nh&acirc;n b&aacute;nh.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Để khu&ocirc;n b&aacute;nh l&ecirc;n bếp v&agrave; cho dầu ăn (mỡ) v&agrave;o tr&aacute;ng từng khu&ocirc;n, đổ bột b&aacute;nh v&agrave;o khu&ocirc;n, cho một &iacute;t hỗn hợp nh&acirc;n v&agrave;o giữa, đậy nắp vung lại cho b&aacute;nh ch&iacute;n l&agrave; lấy ra.</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="DSC01987-JPG-2395-1411115174.jpg" src="http://m.f13.img.vnecdn.net/2014/09/19/DSC01987-JPG-2395-1411115174.jpg" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Khi b&aacute;nh ch&iacute;n, bột trở n&ecirc;n v&agrave;ng đục, c&ograve;n nh&acirc;n m&agrave;u n&acirc;u đỏ, l&aacute; h&agrave;nh trong mỡ dầu b&aacute;m v&agrave;o tạo m&agrave;u xanh.. B&aacute;nh ch&iacute;n cho ra đĩa thưởng thức c&ugrave;ng nước mắm chua ngọt, rau sống v&agrave; đồ chua rất ngon.</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align: center;"><img alt="DSC01992-JPG-6257-1411115174.jpg" src="http://m.f13.img.vnecdn.net/2014/09/19/DSC01992-JPG-6257-1411115174.jpg" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style="text-align: center;">Thưởng thức c&ugrave;ng nước mắm chua ngọt v&agrave; rau sống.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 17000, 1462024309, 1462024309, 0, NULL, NULL),
(8, 3, 'Bò lá lốt', 'bo_la_lot.jpg', 'Bò nướng lá lốt hay còn gọi là bò lá lốt, thịt bò lá lốt là một món ăn trong ẩm thực Việt Nam', '<h2><strong>C&aacute;ch l&agrave;m b&ograve; nướng l&aacute; lốt đơn giản theo c&ocirc;ng thức thứ 1:</strong></h2>\r\n\r\n<p><strong>Nguy&ecirc;n liệu cần chuẩn bị cho m&oacute;n b&ograve; cuốn l&aacute; lốt</strong>: 1 b&oacute; l&aacute; lốt, 300g thịt b&ograve;, 1 tr&aacute;i dưa leo, 1 tr&aacute;i c&agrave; chua, Nước tương, ti&ecirc;u, bột n&ecirc;m, dầu ăn, ớt, tỏi, h&agrave;nh củ&hellip;</p>\r\n\r\n<p>Bạn n&ecirc;n chọn loại thịt b&ograve; dẻ sườn: 400g, bạn cũng c&oacute; thể sử dụng thịt b&ograve; thăn, thịt b&ograve; bắp nhưng b&ograve; dẻ sườn sẽ gi&uacute;p m&oacute;n&nbsp;<strong>b&ograve; nướng l&aacute; lốt</strong>&nbsp;ngon hơn, b&eacute;o ngậy hơn rất nhiều. Trường hợp bạn sử dụng thịt b&ograve; thăn th&igrave; chỉ n&ecirc;n d&ugrave;ng 300g kết hợp 100g mỡ heo nh&eacute;.</p>\r\n\r\n<h3><strong>Hướng dẫn chi tiết&nbsp;<em>c&aacute;ch l&agrave;m b&ograve; cuốn l&aacute; lốt</em>:</strong></h3>\r\n\r\n<p><img alt="bò cuốn lá lốt" src="http://mecuben.com/wp-content/uploads/2015/11/b%C3%B2-cu%E1%BB%91n-l%C3%A1-l%E1%BB%91t-480x474.jpg" style="height:474px; width:480px" /></p>\r\n\r\n<p><strong>Bước 1: Sơ chế nguy&ecirc;n liệu để l&agrave;m m&oacute;n b&ograve; nướng l&aacute; lốt</strong>: H&agrave;nh t&iacute;m b&oacute;c vỏ rửa sạch, băm nhỏ. Dưa leo cắt khoanh tr&ograve;n mỏng, d&ugrave;ng để trang tr&iacute; v&agrave; ăn k&egrave;m với b&ograve; cuốn. Thịt b&ograve; rửa sạch, th&aacute;i ra, xay nhuyễn (băm nhuyễn) L&aacute; lốt nhặt v&agrave; rửa sạch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 2 Ướp thịt</strong>: Ướp thịt b&ograve; đ&atilde; xay nhuyễn&nbsp;với bột n&ecirc;m, ti&ecirc;u, h&agrave;nh t&iacute;m&nbsp;băm nhỏ rồi trộn đều l&ecirc;n. Ướp thịt b&ograve; để l&agrave;m b&ograve; nướng l&aacute; lốt: Cho v&agrave;o t&ocirc; lớn c&aacute;c nguy&ecirc;n liệu gồm thịt bỏ dẻ sườn, sả, gừng, h&agrave;nh kh&ocirc;, tỏi, ớt, l&aacute; lốt đ&atilde; băm nhỏ c&ugrave;ng với 1,5 th&igrave;a hạt n&ecirc;m, 1 th&igrave;a nước mắm, 1 th&igrave;a bột ngọt, 1 th&igrave;a ti&ecirc;u bột, &frac12; th&igrave;a dầu ăn, trộn đều, d&ugrave;ng tay c&oacute; đeo găng tay nilon b&oacute;p kỹ, ấn nhẹ, để nguy&ecirc;n trong điều kiện thường 30 ph&uacute;t l&agrave; thịt ngấm ho&agrave;n to&agrave;n gia vị nh&eacute;. Thịt b&ograve; c&oacute; ngấm gia vị th&igrave;&nbsp;<strong>c&aacute;ch l&agrave;m thịt b&ograve; nướng l&aacute; lốt</strong>&nbsp;mới thơm ngon, tr&ograve;n vị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 3 Cuộn thịt</strong>: Trải l&aacute; lốt ra dĩa cho thịt b&ograve; v&agrave;o cuộn lại. Bạn trải l&aacute; lốt ra, cho 1 th&igrave;a nhỏ thịt v&agrave;o mặt ngo&agrave;i l&aacute; lốt, cuốn tr&ograve;n lại, c&oacute; thể d&ugrave;ng tăm để xi&ecirc;n qua giữ cho&nbsp;<strong>b&ograve; nướng&nbsp;l&aacute; lốt</strong>&nbsp;được định vị hay d&ugrave;ng l&aacute; h&agrave;nh chần qua nước s&ocirc;i để cột lại nh&eacute;. Thực hiện lần lượt cho đến khi hết nguy&ecirc;n liệu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 4 Nướng thịt</strong>: Đem nướng hoặc dầu v&agrave;o chảo, dầu n&oacute;ng&nbsp;cho từng cuộn thịt b&ograve; v&agrave;o chi&ecirc;n ch&iacute;n. Bạn c&oacute; thể lưu &yacute; c&aacute;ch nướng b&ograve; l&aacute; lốt khi sử dụng l&ograve; nướng hoặc than dưới đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li><strong>Nướng bằng l&ograve; nướng</strong>: Bạn bật l&ograve; nướng 250 độ trước 5-10 ph&uacute;t. Tiếp đ&oacute;, để c&aacute;ch l&agrave;m b&ograve; nướng l&aacute; lốt được mềm v&agrave; b&eacute;o ngậy bạn h&atilde;y xếp nguy&ecirc;n liệu v&agrave;o khay, d&ugrave;ng cọ qu&eacute;t 1 lớp nhẹ dầu m&egrave; hoặc dầu vừa l&ecirc;n tr&ecirc;n rồi nướng trong v&ograve;ng 15 ph&uacute;t l&agrave; đạt y&ecirc;u cầu.</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong>Nướng bằng than</strong>: C&aacute;ch l&agrave;m b&ograve; nướng l&aacute; lốt ngon đ&uacute;ng điệu nhất l&agrave; nướng bằng than củi đ&oacute; c&aacute;c bạn ạ. Bạn tiến h&agrave;nh quạt than đỏ, rồi xếp b&ograve; nướng l&aacute; lốt l&ecirc;n vỉ nướng, d&ugrave;ng cọ qu&eacute;t một lớp mỏng dầu v&agrave; cho l&ecirc;n bếp than nướng, n&ecirc;n giữ than ở nhiệt độ vừa phải, trở đều tay khi nướng để m&oacute;n b&ograve; cuốn l&aacute; lốt ch&iacute;n đều m&agrave; kh&ocirc;ng bị ch&aacute;y nh&eacute;. Nướng khoảng 20 ph&uacute;t v&agrave; thấy dậy m&ugrave;i thơm hấp dẫn l&agrave; đảm bảo rồi đấy.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gắp b&ograve; ra&nbsp;dĩa, trang tr&iacute; với hoa c&agrave; chua dưa leo&nbsp;cho đẹp mắt. M&oacute;n n&agrave;y ăn kem với nước tương (muốn ăn cay th&igrave; cho th&ecirc;m tỏi v&agrave; ớt v&agrave;o nước tương).</p>\r\n\r\n<p>Như vậy l&agrave; bạn đ&atilde; ho&agrave;n th&agrave;nh m&oacute;n B&ograve; cuốn l&aacute; lốt theo c&aacute;ch 1 rồi đấy, tham khảo th&ecirc;m 1 c&aacute;ch l&agrave;m nh&eacute;:</p>\r\n\r\n<h2><strong>C&aacute;ch l&agrave;m b&ograve; cuốn l&aacute; lốt&nbsp;theo c&ocirc;ng thức thứ 2:</strong></h2>\r\n\r\n<p><strong>Nguy&ecirc;n liệu cần chuẩn bị cho m&oacute;n ăn n&agrave;y</strong>: 400g thịt b&ograve; băm, 200g thịt nạc dăm (hơi nhiều mỡ) băm, 1 b&oacute; l&aacute; lốt, Sả băm</p>\r\n\r\n<p><strong>Bước 1</strong>: Thịt b&ograve;, thịt heo trộn đều với gia vị: 1 muỗng caf&eacute; muối, 2 muỗng caf&eacute; đường, 1 muỗng caf&eacute; bột ngọt, 2 muỗng canh dầu ăn, 1 muỗng canh nước tương, 2 muỗng canh tỏi băm, 2 muỗng canh sả băm, 1 muỗng caf&eacute; bột ngũ vị hương (nếu th&iacute;ch).</p>\r\n\r\n<p><strong>Bước 2</strong>: L&aacute; lốt ngắt rời từng l&aacute; (nhớ chừa lại phần cuống l&aacute;), rửa sạch, để r&aacute;o nước v&agrave; hơi h&eacute;o sẽ dễ cuộn hơn.</p>\r\n\r\n<p><strong>Bước 3</strong>: Trải l&aacute; ra mặt phẳng (phần l&aacute; nhạt m&agrave;u tiếp x&uacute;c với thịt, phần l&aacute; đậm m&agrave;u quay ra ngo&agrave;i), cho lượng vừa đủ thịt l&ecirc;n mặt l&aacute; (cho nhiều qu&aacute; thịt sẽ kh&oacute; ch&iacute;n).Gập 2 m&eacute;p l&aacute; v&agrave; cuộn lại.&nbsp; D&ugrave;ng phần cuống l&aacute; ghim v&agrave;o để cố định. L&agrave;m lần lượt cho hết, cho thịt v&agrave;o hộp, để tủ lạnh khi n&agrave;o d&ugrave;ng th&igrave; lấy ra.</p>\r\n\r\n<p><img alt="" src="http://mecuben.com/wp-content/uploads/2015/11/cach-lam-cha-la-lot-4-1.jpg" style="height:373px; width:549px" /></p>\r\n\r\n<p><img alt="" src="http://mecuben.com/wp-content/uploads/2015/11/SEOUL-GARDEN-BUFFET-TRUA-NUONG-VA-LAU-KHONG-KHOI_20121016155042334.jpg" style="height:462px; width:748px" /></p>\r\n\r\n<p><strong>Bước 4</strong>: Cuộn b&ograve; c&oacute; thể đem nướng tr&ecirc;n than hoa hay l&ograve; nướng hoặc &aacute;p chảo đều ngon cả. Khi nướng hay &aacute;p chảo nhớ phết dầu ăn l&ecirc;n để cuộn b&ograve; kh&ocirc;ng bị kh&ocirc; ch&aacute;y.</p>\r\n\r\n<p><img alt="" src="http://mecuben.com/wp-content/uploads/2015/11/cha-la-lot-3.jpg" style="height:248px; width:400px" /></p>\r\n\r\n<p><strong>Bước 5</strong>:&nbsp;<strong>Pha mắm n&ecirc;m</strong>: 2 muỗng canh mắm n&ecirc;m, 4 muỗng canh đường, &frac12; tr&aacute;i thơm bằm nhuyễn, 1 muỗng canh dấm, 4 muỗng canh nước, 4 t&eacute;p tỏi băm, 1 tr&aacute;i ớt băm, 1 muỗng cafe riềng băm. Khuấy đều tất cả, n&ecirc;m cho vừa ăn.</p>\r\n\r\n<p><img alt="" src="http://mecuben.com/wp-content/uploads/2015/11/bo-cuon-la-lot1.jpg" style="height:412px; width:550px" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 6</strong>: Dọn b&ograve; l&aacute; lốt k&egrave;m rau sống, đồ chua, dưa leo, thơm, khế, củ kiệu, chuối ch&aacute;t, b&uacute;n, b&aacute;nh tr&aacute;ng v&agrave; ch&eacute;n mắm n&ecirc;m, ăn đến đ&acirc;u cuốn đến đấy. C&aacute;ch n&agrave;y th&iacute;ch hợp cho c&aacute;c buổi họp mặt, vừa nướng, vừa cuốn, vừa h&agrave;n huy&ecirc;n. Nếu muốn nhanh, c&oacute; thể b&agrave;y một t&ocirc; b&uacute;n b&ograve; l&aacute; lốt, chan mắm n&ecirc;m, trộn đều rồi thưởng thức.</p>\r\n', 25000, 1462024308, 1462024308, 0, NULL, NULL),
(10, 1, 'Bánh mì kẹp thịt', 'banh_my1462024312.jpg', 'Bánh mì bơ tỏi đã ngon rồi, nay còn nhồi thêm steak nữa!', '<p>Thật ra, đ&acirc;y l&agrave; m&oacute;n ăn của người Khmer ở tận khu vực Bảy N&uacute;i, Tịnh Bi&ecirc;n (An Giang), cứ thế lan dần v&agrave;o Long Xuy&ecirc;n, rồi đến c&aacute;c tỉnh t&acirc;y nam bộ kh&aacute;c v&agrave; b&acirc;y giờ l&agrave; l&ecirc;n tận S&agrave;i G&ograve;n v&agrave; Đ&agrave; Lạt. Cứ mỗi lần nhảy c&oacute;c l&ecirc;n một địa b&agrave;n mới, m&oacute;n ăn được biến tấu kh&aacute;c đi một ch&uacute;t theo &yacute; th&iacute;ch của người b&aacute;n v&agrave; phong vị thực kh&aacute;ch ở từng nơi.</p>\r\n\r\n<p>C&aacute;i t&ecirc;n b&aacute;nh m&igrave; nướng muối ớt m&ocirc; tả nguy&ecirc;n thủy c&aacute;ch chế biến của người d&acirc;n Tịnh Bi&ecirc;n, tức l&agrave; chỉ c&oacute; b&aacute;nh m&igrave; quết muối ớt b&ecirc;n ngo&agrave;i v&agrave; đem nướng gi&ograve;n tr&ecirc;n bếp than. Khi đổ bộ đến c&aacute;c địa phương kh&aacute;c, m&oacute;n ăn được &quot;n&acirc;ng cấp sang chảnh&quot; bằng c&aacute;ch bổ sung th&ecirc;m bơ, nước xốt sa tế phết b&ecirc;n ngo&agrave;i. Khi nướng n&oacute;ng gi&ograve;n l&ecirc;n th&igrave; b&aacute;nh được ăn k&egrave;m với nhiều m&oacute;n nữa như ph&ocirc; mai, ch&agrave; b&ocirc;ng, ruốc t&eacute;p, mỡ h&agrave;nh, xốt majonnaise, tương ớt thậm ch&iacute; cả x&uacute;c x&iacute;ch v&agrave; pa t&ecirc; t&ugrave;y mỗi qu&aacute;n.</p>\r\n\r\n<p><img alt="" id="img_346441" src="http://giadinh.vcmedia.vn/k:2016/2-banhmi1-xxnu-1461916669030/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>D&ugrave;ng thớt để &eacute;p b&aacute;nh m&igrave; cho dẹp</p>\r\n\r\n<p><img alt="" id="img_346440" src="http://giadinh.vcmedia.vn/k:2016/3-banhmi-nuong3-fmsv-1461916667444/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>Quết bơ v&agrave; nước xốt gia vị cho lớp vỏ ngo&agrave;i đậm đ&agrave; hơn theo phong c&aacute;ch ăn của người S&agrave;i G&ograve;n</p>\r\n\r\n<p><img alt="Bánh mì chuẩn bị được đem đi nướng" id="img_346439" src="http://giadinh.vcmedia.vn/k:2016/4-banhmi3-sict-1461916665376/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>B&aacute;nh m&igrave; chuẩn bị được đem đi nướng</p>\r\n\r\n<p>D&ugrave; l&agrave; m&oacute;n ăn n&oacute;ng v&agrave; trời S&agrave;i G&ograve;n đương trong m&ugrave;a &quot;đổ mồ h&ocirc;i hột&quot; của th&aacute;ng tư nhưng người S&agrave;i G&ograve;n vẫn đổ x&ocirc; đi ăn b&aacute;nh m&igrave; nướng muối ớt như một tr&agrave;o lưu mới kh&oacute; cưỡng. Tr&ecirc;n mạng, c&aacute;c bạn trẻ đ&atilde; h&agrave;o ph&oacute;ng d&agrave;nh tặng những t&iacute;nh từ như &ldquo;si&ecirc;u cấp&rdquo;, &ldquo;thần th&aacute;nh&rdquo;, &ldquo;ngon qu&ecirc;n sầu&rdquo; để v&iacute; von về m&oacute;n ăn vặt mới toanh n&agrave;y.</p>\r\n\r\n<p>Chị Đ&ocirc;ng Anh, một chủ tiệm b&aacute;nh m&igrave; nướng muối ớt tr&ecirc;n đường L&ecirc; Quang Sung (quận 6) n&oacute;i vui: &ldquo;M&oacute;n ăn n&agrave;y cũng giống như xo&agrave;i lắc vậy đ&oacute;. Người ăn theo phong tr&agrave;o n&ecirc;n người b&aacute;n cũng phải nhanh tay tranh thủ hốt kh&aacute;ch chứ!&quot;.</p>\r\n\r\n<p>Cả con đường L&ecirc; Quang Sung c&oacute; đ&acirc;u chừng tr&ecirc;n dưới s&aacute;u tiệm b&aacute;n b&aacute;nh m&igrave; nướng muối ớt m&agrave; hỏi ra tiệm n&agrave;o cũng b&aacute;n được cả hơn bốn trăm ổ một ng&agrave;y. Ri&ecirc;ng tiệm C&ocirc; T&aacute;m nằm ngay ng&atilde; ba L&ecirc; Quang Sung v&agrave; Cao Văn Lầu th&igrave; số lượng c&oacute; khi gấp đến mấy lần. Chiều n&agrave;o trước cửa tiệm n&agrave;y cũng ồn nhất phố v&agrave; l&agrave;m kẹt cả một g&oacute;c đường v&igrave; ai cũng muốn đợi để mua cho kỳ được phần b&aacute;nh m&igrave; nướng muối ớt hấp dẫn.</p>\r\n\r\n<p>Cho đến b&acirc;y giờ, kh&ocirc;ng chỉ c&oacute; phố L&ecirc; Quang Sung (quận 6) m&agrave; nhiều khu vực kh&aacute;c như tr&ecirc;n đường Điện Bi&ecirc;n Phủ (khu vực quận B&igrave;nh Thạnh) , T&ocirc; Hiến Th&agrave;nh (quận 10) , Lam Sơn, Minh Phụng, H&agrave;n Hải Nguy&ecirc;n (quận 11) cũng đ&atilde; rộn r&agrave;ng tấp nập với m&oacute;n ăn n&agrave;y&hellip;</p>\r\n\r\n<p><img alt="" id="img_346438" src="http://giadinh.vcmedia.vn/k:2016/5-banhmi4-qlyl-1461916663077/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>Gia đ&igrave;nh c&ocirc; Nga, ngay g&oacute;c ng&atilde; tư Cao Văn Lầu &ndash; L&ecirc; Quang Sung l&agrave; qu&aacute;n mới nhất tr&ecirc;n con phố &ldquo;b&aacute;nh m&igrave; nướng muối ớt&quot; n&agrave;y</p>\r\n\r\n<p><img alt="" id="img_346437" src="http://giadinh.vcmedia.vn/k:2016/6-banhmi-nuong2-biuy-1461916662183/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>B&aacute;nh được nướng gi&ograve;n tr&ecirc;n bếp than khi c&oacute; kh&aacute;ch mua</p>\r\n\r\n<p><img alt="" id="img_346436" src="http://giadinh.vcmedia.vn/k:2016/7-banhmi6-pbep-1461916661329/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>Mặc d&ugrave; đ&ocirc;ng nhưng ai cũng ki&ecirc;n nhẫn chờ để tới lượt m&igrave;nh ở tiệm C&ocirc; T&aacute;m</p>\r\n\r\n<p><img alt="" id="img_346435" src="http://giadinh.vcmedia.vn/k:2016/8-banhmi7-qceb-1461916659761/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>Nhiều gia đ&igrave;nh tranh thủ mặt bằng trước nh&agrave; mở tiệm b&aacute;n theo tr&agrave;o lưu</p>\r\n\r\n<p><img alt="" id="img_346434" src="http://giadinh.vcmedia.vn/k:2016/9-banhmi-nuong4-beml-1461916658178/sai-gon-lai-ron-rang-voi-mon-banh-mi-nuong-muoi-ot.jpg" /></p>\r\n\r\n<p>Mỗi qu&aacute;n c&oacute; b&iacute; quyết ri&ecirc;ng của m&igrave;nh, c&oacute; khi l&agrave; th&ecirc;m x&iacute;ch x&uacute;c hoặc pa t&ecirc;. Một phần b&aacute;nh m&igrave; nướng muối ớt ngon l&agrave;nh thế n&agrave;y, ai nh&igrave;n m&agrave; kh&ocirc;ng th&egrave;m!</p>\r\n', 11000, 1462024312, 1462439027, 1, NULL, 1),
(11, 2, 'Bánh xèo', 'banh_xeo.jpg', 'Bánh xèo là một loại bánh Việt Nam, có bột bên ngoài, bên trong có nhân là tôm, thịt, giá đỗ,', '<h2>Nguy&ecirc;n liệu l&agrave;m b&aacute;nh x&egrave;o</h2>\r\n\r\n<p><strong>Nước chấm:</strong></p>\r\n\r\n<p>&ndash; 3 th&igrave;a nước mắm</p>\r\n\r\n<p>&ndash; 1/2 th&igrave;a nước s&ocirc;i</p>\r\n\r\n<p>&ndash; 1 th&igrave;a đường</p>\r\n\r\n<p>&ndash; 1/2 th&igrave;a bột ngọt</p>\r\n\r\n<p>&ndash; 2 tr&aacute;i ớt băm nhuyễn</p>\r\n\r\n<p>&ndash; 1 th&igrave;a tỏi</p>\r\n\r\n<p>&ndash; 1 th&igrave;a nước cốt chanh</p>\r\n\r\n<p><strong>Vỏ b&aacute;nh x&egrave;o:</strong></p>\r\n\r\n<p>&ndash; 200g bột gạo</p>\r\n\r\n<p>&ndash; 250ml nước</p>\r\n\r\n<p>&ndash; 100ml bia</p>\r\n\r\n<p>&ndash; 50ml nước cốt dừa</p>\r\n\r\n<p>&ndash; &frac12; th&igrave;a caf&eacute; muối</p>\r\n\r\n<p>&ndash; 1/2 th&igrave;a caf&eacute; bột nghệ</p>\r\n\r\n<p>&ndash; H&agrave;nh l&aacute;, dầu ăn</p>\r\n\r\n<p><strong>Nh&acirc;n b&aacute;nh x&egrave;o:</strong></p>\r\n\r\n<p>&ndash; 200g thịt b&ograve;</p>\r\n\r\n<p>&ndash; 200g t&ocirc;m</p>\r\n\r\n<p>&ndash; 1 củ h&agrave;nh t&acirc;y</p>\r\n\r\n<p>&ndash; 100g gi&aacute; đỗ</p>\r\n\r\n<p>&ndash; Gừng, h&agrave;nh kh&ocirc;, ti&ecirc;u, gia vị</p>\r\n\r\n<p>&ndash; Rau sống, x&agrave; l&aacute;ch,&hellip;</p>\r\n\r\n<h2>C&aacute;ch l&agrave;m b&aacute;nh x&egrave;o</h2>\r\n\r\n<p><strong>Bước 1</strong></p>\r\n\r\n<p>-Trộn đều 3 th&igrave;a nước mắm, 1/2 th&igrave;a nước s&ocirc;i, 1 th&igrave;a đường, 1/2 th&igrave;a bột ngọt, 2 tr&aacute;i ớt băm nhuyễn, 1 th&igrave;a tỏi đến khi đường v&agrave; bột ngọt tan đều rồi cho 1 th&igrave;a chanh v&agrave;o sao cho vừa ăn v&agrave; hợp khẩu vị.</p>\r\n\r\n<p><strong>Bước 2:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ndash; Trộn đều bột gạo, muối, bột nghệ trong một chiếc t&ocirc; lớn. Pha nước với bia rồi từ từ đổ v&agrave;o phần bột, trộn đểu. Th&ecirc;m nước cốt dừa v&agrave;o, trộn thật đều. Khuấy bột đến khi thu được hỗn hợp bột lỏng, mịn, kh&ocirc;ng v&oacute;n cục.</p>\r\n\r\n<p>&ndash; Cắt kh&uacute;c h&agrave;nh l&aacute; rồi cho v&agrave;o phần bột vừa trộn rồi th&ecirc;m một ch&uacute;t dầu ăn, để bột nghỉ khoảng 30 ph&uacute;t.</p>\r\n\r\n<p><a href="http://lambanh365.com/wp-content/uploads/2015/03/cach-lam-banh-xeo-1.jpg"><img alt="cách làm bánh xèo 1, cach lam banh xeo 1" src="http://lambanh365.com/wp-content/uploads/2015/03/cach-lam-banh-xeo-1.jpg" style="height:412px; width:550px" /></a></p>\r\n\r\n<p><strong>Bước 3:</strong></p>\r\n\r\n<p>&ndash; Rửa sạch c&aacute;c loại rau. H&agrave;nh t&acirc;y th&aacute;i m&uacute;i cau.</p>\r\n\r\n<p>&ndash; Nhặt t&ocirc;m, rửa sạch. Thịt b&ograve; th&aacute;i l&aacute;t mỏng.</p>\r\n\r\n<p>&ndash; Ướp t&ocirc;m với gừng, gia vị, h&agrave;nh kh&ocirc;. Ướp thịt b&ograve; với dầu ăn, tỏi, gừng, gia vị, hạt ti&ecirc;u.</p>\r\n\r\n<p><strong>Bước 4:</strong></p>\r\n\r\n<p>-L&agrave;m n&oacute;ng chảo với dầu ăn, cho h&agrave;nh t&acirc;y v&agrave;o x&agrave;o thơm với một ch&uacute;t gia vị. Tiếp theo cho t&ocirc;m v&agrave;o đảo c&ugrave;ng. Khi t&ocirc;m gần ch&iacute;n th&igrave; cho thịt b&ograve; v&agrave;o, hỗn hợp nh&acirc;n ch&iacute;n bạn m&uacute;c ra để ri&ecirc;ng.</p>\r\n\r\n<p><strong>Bước 5:</strong></p>\r\n\r\n<p>&ndash; Cho dầu ăn v&agrave;o chảo, chờ dầu s&ocirc;i. Đổ bột l&aacute;ng xung quanh chảo cho thật mỏng, đậy vung khoảng nửa ph&uacute;t.</p>\r\n\r\n<p>&ndash; Cho phần nh&acirc;n v&agrave; gi&aacute; l&ecirc;n tr&ecirc;n b&aacute;nh, đậy vung th&ecirc;m khoảng 2 ph&uacute;t.</p>\r\n\r\n<p><a href="http://lambanh365.com/wp-content/uploads/2015/03/cach-lam-banh-xeo-2.jpg"><img alt="cách làm bánh xèo 2, cach lam banh xeo 2" src="http://lambanh365.com/wp-content/uploads/2015/03/cach-lam-banh-xeo-2.jpg" style="height:365px; width:550px" /></a></p>\r\n\r\n<p>&ndash; Gập đ&ocirc;i b&aacute;nh, r&aacute;n b&aacute;nh đến khi b&aacute;nh gi&ograve;n, v&agrave;ng đều 2 mặt.</p>\r\n\r\n<p><a href="http://lambanh365.com/wp-content/uploads/2015/03/cach-lam-banh-xeo-3.png"><img alt="cách làm bánh xèo 3, cach lam banh xeo 3" src="http://lambanh365.com/wp-content/uploads/2015/03/cach-lam-banh-xeo-3.png" style="height:367px; width:550px" /></a></p>\r\n\r\n<p><strong>Bước 6:</strong></p>\r\n\r\n<p>&ndash; D&ugrave;ng b&aacute;nh khi c&ograve;n n&oacute;ng. Cuộn b&aacute;nh với rau sống, chấm&nbsp;<a href="http://lambanh365.com/cach-lam/cong-thuc-pha-nuoc-cham-banh-xeo-doc-dao/">nước chấm b&aacute;nh x&egrave;o</a>&nbsp;chua ngọt.</p>\r\n\r\n<p>Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng với c&aacute;ch l&agrave;m<strong>&nbsp;b&aacute;nh x&egrave;o</strong>&nbsp;n&agrave;y!</p>\r\n', 30000, 1462171618, 1462171618, 1, NULL, 26),
(12, 1, 'Bông cải bò', 'bong_cai_bo.jpg', 'Bông cải xào vừa chín đến, giòn sần sật, thịt bò chín mềm thấm đẫm gia vị, ăn kèm cơm trắng vừa ngon miệng lại', '<p>Nguy&ecirc;n liệu:</p>\r\n\r\n<p>- 1 b&ocirc;ng cải trắng, 1 b&ocirc;ng cải xanh loại nhỏ, 2 củ c&agrave; rốt.</p>\r\n\r\n<p>- 400 g thịt b&ograve;, ti&ecirc;u bột, đường, hạt n&ecirc;m, nước tương, tỏi bằm.</p>\r\n\r\n<p>C&aacute;ch chế biến:</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="bong-cai-1-7212-1381313157.jpg" src="http://m.f13.img.vnecdn.net/2013/10/09/bong-cai-1-7212-1381313157.jpg" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>- B&ocirc;ng cải th&aacute;i nhỏ, c&agrave; rốt gọt vỏ, th&aacute;i nhỏ rồi rửa với nước muối, rửa lại nhiều lần bằng nước sạch.</p>\r\n\r\n<p>- Chần c&aacute;c loại rau củ sơ qua nước s&ocirc;i, vớt ra để r&aacute;o. Sau đ&oacute; bạn ướp rau củ với 1 th&igrave;a c&agrave; ph&ecirc; hạt n&ecirc;m, &iacute;t muối, đường rồi trộn đều.</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="bong-cai-2-5481-1381313157.jpg" src="http://m.f13.img.vnecdn.net/2013/10/09/bong-cai-2-5481-1381313157.jpg" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>- Thịt b&ograve; rửa sạch, th&aacute;i l&aacute;t, ướp với đường, nước tương, hạt n&ecirc;m trộn thật đều rồi cho v&agrave;o &iacute;t dầu ăn.</p>\r\n\r\n<p>- Phi thơm tỏi, cho thịt b&ograve; v&agrave;o x&agrave;o nhanh với lửa lớn, tiếp đến cho rau củ v&agrave;o x&agrave;o ch&iacute;n.</p>\r\n\r\n<table align="center" border="0" cellpadding="3" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt="bong-cai-3-3554-1381313158.jpg" src="http://m.f13.img.vnecdn.net/2013/10/09/bong-cai-3-3554-1381313158.jpg" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>- B&ocirc;ng cải x&agrave;o ch&iacute;n cho ra đĩa, rắc l&ecirc;n &iacute;t ti&ecirc;u bột rồi d&ugrave;ng n&oacute;ng.</p>\r\n', 19000, 1462172100, 1462439003, 1, NULL, 21),
(14, 5, 'Chè bưởi', 'che_buoi.jpg', 'Chè bưởi là một trong những món ăn ngon để tráng miệng , giản dị, gần gũi gắn bó với tuổi thơ', '<p>Chắc hẳn m&oacute;n&nbsp;<strong>ch&egrave; bưởi</strong>&nbsp;với hương vị ngọt ngọt, thanh thanh, gi&ograve;n ngon hấp dẫn đ&atilde; khiến rất nhiều người y&ecirc;u th&iacute;ch ăn ho&agrave;i kh&ocirc;ng ng&aacute;n phải kh&ocirc;ng n&agrave;o v&agrave; việc chế biến&nbsp;<a href="http://cachlam9.com/chuyen-muc/cach-lam-che" target="_blank">m&oacute;n ch&egrave;</a>&nbsp;n&agrave;y cũng kh&ocirc;ng kh&oacute; như bạn nghĩ đ&acirc;u nh&eacute;, chỉ cần thực hiện đ&uacute;ng&nbsp;<strong>c&aacute;ch nấu ch&egrave; bưởi cực ngon</strong>&nbsp;dưới đ&acirc;y l&agrave; bạn đ&atilde; mang đến cho cả gia đ&igrave;nh đặc biệt l&agrave; c&aacute;c b&eacute; y&ecirc;u một m&oacute;n ăn vặt vừa hấp dẫn vừa ngon miệng lại vừa đảm bảo an to&agrave;n thực phẩm nữa đấy nh&eacute;.</p>\r\n\r\n<h2>C&aacute;ch nấu m&oacute;n ch&egrave; bưởi</h2>\r\n\r\n<h3>Chuẩn bị nguy&ecirc;n liệu nấu m&oacute;n ch&egrave; bưởi</h3>\r\n\r\n<ol>\r\n	<li>Bưởi: 1 quả to hoặc 2 quả nhỏ, bạn n&ecirc;n chọn bưởi da xanh hoặc bưởi năm roi cho ngon nh&eacute;.</li>\r\n	<li>Đậu xanh đ&atilde; x&aacute;t vỏ: 250g.</li>\r\n	<li>Đường: 300g.</li>\r\n	<li>Muối: 4 th&igrave;a.</li>\r\n	<li>Nước cốt dừa: 1 hộp.</li>\r\n	<li>Sữa tươi c&oacute; đường: 200ml.</li>\r\n	<li>Bột năng: 50g.</li>\r\n	<li>Đậu phộng rang sẵn: 100g.</li>\r\n	<li>Vani: 3 ống, bạn cũng c&oacute; thể sử dụng hoa bưởi để thay thế nhưng vani sẽ tiện dụng hơn nh&eacute;.</li>\r\n</ol>\r\n\r\n<h3>Sơ chế nguy&ecirc;n liệu nấu m&oacute;n ch&egrave; bưởi</h3>\r\n\r\n<p><strong>Đậu xanh:</strong>&nbsp;Ng&acirc;m trong nước lạnh 1 tiếng, rồi vớt ra rổ để r&aacute;o, trong qu&aacute; tr&igrave;nh chờ ng&acirc;m đậu xanh bạn chuyển qua sơ chế l&agrave;m c&ugrave;i bưởi nh&eacute;;</p>\r\n\r\n<p><strong>L&agrave;m c&ugrave;i bưởi &ndash; phần ch&iacute;nh của m&oacute;n ch&egrave; bưởi:</strong></p>\r\n\r\n<p>+ Gọt lớp vỏ xanh b&ecirc;n ngo&agrave;i quả bưởi, giữ phần c&ugrave;i trắng, bạn chỉ gọt mỏng vừa th&ocirc;i nh&eacute;, nếu gọt mỏng qu&aacute; th&igrave; tinh vỏ bưởi sẽ l&agrave;m m&oacute;n&nbsp;<strong>ch&egrave; bưởi</strong>&nbsp;bị đắng đấy, phần c&ugrave;i trắng bạn th&aacute;i hạt lựu 1cm;</p>\r\n\r\n<p><img alt="Sơ chế nguyên liệu nấu chè bưởi, nguyên liệu để nấu chè bưởi" src="http://cachlam9.com/wp-content/uploads/2014/12/So-che-nguyen-lieu-nau-che-buoi.jpg" style="height:512px; width:512px" /></p>\r\n\r\n<p>+ Trộn đều phần c&ugrave;i trắng bưởi với 2 th&igrave;a muối trong 10 ph&uacute;t, sau đ&oacute; b&oacute;p kỹ nhưng nhẹ tay rồi xả lại bằng nước lạnh nhiều lần cho sạch muối rổi vắt r&aacute;o nước.</p>\r\n\r\n<p>+ Tiếp đ&oacute;, bạn đun s&ocirc;i 1 nồi nước với 1 th&igrave;a muối, cho c&ugrave;i bưởi v&agrave;o luộc sơ v&agrave; thực hiện việc xả kỹ với nước lạnh, vắt r&aacute;o nước như ở tr&ecirc;n, l&agrave;m như thế c&ugrave;i bưởi sẽ kh&ocirc;ng c&ograve;n vị đắng, cay, the nữa.</p>\r\n\r\n<p>+ Ướp c&ugrave;i bưởi với 100g đường trong 2 tiếng v&agrave; lăn kh&ocirc; c&ugrave;i bưởi với 30g bột năng trong 1 t&ocirc; lớn để khi ho&agrave;n th&agrave;nh m&oacute;n ch&egrave; bưởi, ch&iacute;nh lớp bột năng n&agrave;y sẽ gi&uacute;p cho c&ugrave;i bưởi c&oacute; hương vị gi&ograve;n gi&ograve;n, dai dai rất đặc trưng đấy.</p>\r\n\r\n<p>+ Đun s&ocirc;i 1 nồi nước v&agrave; thả c&ugrave;i bưởi v&agrave;o, đến khi c&ugrave;i bưởi nổi l&ecirc;n ph&iacute;a tr&ecirc;n hết v&agrave; c&oacute; m&agrave;u trắng, trong (như bột lọc) th&igrave; vớt ra, cho v&agrave;o t&ocirc; lớn c&oacute; để nước đ&aacute;, ch&iacute;nh nước đ&aacute; sẽ gi&uacute;p c&ugrave;i bưởi cứng v&agrave; gi&ograve;n hơn rất nhiều. Sau khi ng&acirc;m trong nước đ&aacute; 15 ph&uacute;t, bạn vớt ra rổ, để r&aacute;o.</p>\r\n\r\n<p><strong>L&agrave;m nước cốt dừa ăn k&egrave;m m&oacute;n ch&egrave; bưởi:</strong>&nbsp;Cho 200ml sữa tươi kh&ocirc;ng đường với hộp nước cốt dừa đến khi hỗn hợp sền sệt th&igrave; tắt bếp, để nguội, l&agrave;m như thế nước cốt dừa sẽ b&eacute;o ngậy v&agrave; thơm hơn rất nhiều;</p>\r\n\r\n<p><strong>Đậu phộng rang sẵn:</strong>&nbsp;Xa sạch vỏ, gi&atilde; nhỏ.</p>\r\n\r\n<h3>Thực hiện nấu m&oacute;n ch&egrave; bưởi</h3>\r\n\r\n<p>&ndash; Cho 2,5 l&iacute;t nước v&agrave;o nồi đun s&ocirc;i c&ugrave;ng với 150g đường v&agrave; 250g đậu xanh, bạn c&oacute; thể gia giảm lượng đường t&ugrave;y theo khẩu vị nh&eacute;, đến khi nước s&ocirc;i, bạn h&ograve;a 20g bột năng c&ograve;n lại với 1 b&aacute;t nước lạnh v&agrave; cho v&agrave;o nồi ch&egrave;, vừa đổ vừa khuấy để bột năng tan đều, tiếp tục khuấy đến khi nước ch&egrave; bắt đầu s&aacute;nh lại;</p>\r\n\r\n<p>&ndash; Khi thấy đậu xanh vừa ch&iacute;n mềm, nở ra, bạn cho c&ugrave;i bưởi đ&atilde; sơ chế v&agrave;o, khuấy nhẹ tay, vặn lửa nhỏ vừa, đun s&ocirc;i tiếp 5 ph&uacute;t, cho 3 ống vani v&agrave;o rồi tắt bếp. Bạn cũng c&oacute; thể cho l&aacute; dứa v&agrave;o đun c&ugrave;ng với ch&egrave; để tạo m&ugrave;i thơm y&ecirc;u th&iacute;ch nh&eacute;, đến khi ch&egrave; ch&iacute;n th&igrave; vớt l&aacute; dứa ra l&agrave; đ&atilde; c&oacute; thể thưởng thức m&oacute;n&nbsp;<strong>ch&egrave; bưởi</strong>&nbsp;hấp dẫn n&agrave;y rồi.</p>\r\n\r\n<p><img alt="Cách nấu chè bưởi ngon nhất" src="http://cachlam9.com/wp-content/uploads/2014/12/Thuc-hien-nau-che-buoi.jpg" style="height:300px; width:450px" /></p>\r\n\r\n<p>C&aacute;ch nấu ch&egrave; bưởi ngon nhất</p>\r\n\r\n<h3>Y&ecirc;u cầu v&agrave; thưởng thức m&oacute;n ch&egrave; bưởi</h3>\r\n\r\n<ul>\r\n	<li>&ndash; M&oacute;n&nbsp;<strong>ch&egrave; bưởi</strong>&nbsp;c&oacute; vị ngọt thanh vừa ăn, c&oacute; m&ugrave;i thơm đặc trưng rất hấp dẫn, c&ugrave;i bưởi ngọt nhẹ, gi&ograve;n, dai, đậu xanh ch&iacute;n mềm g&oacute;p phần l&agrave;m n&ecirc;n vị ngon đặc trưng của m&oacute;n ch&egrave; hấp dẫn n&agrave;y.</li>\r\n	<li>&ndash; Nước&nbsp;<strong>ch&egrave; bưởi</strong>&nbsp;sền sệt, khi ăn k&egrave;m với nước cốt dừa v&agrave; đậu phộng rang sẵn rất thơm ngon v&agrave; k&iacute;ch th&iacute;ch khẩu vị hiệu quả</li>\r\n	<li><strong>&ndash; C&aacute;ch thưởng thức m&oacute;n ch&egrave; bưởi ngon đ&uacute;ng chuẩn:</strong>&nbsp;Với m&oacute;n ch&egrave; n&agrave;y, bạn c&oacute; thể ăn n&oacute;ng, ăn lạnh t&ugrave;y theo sở th&iacute;ch v&agrave; thời tiết nh&eacute;:</li>\r\n	<li>Ăn n&oacute;ng: M&uacute;c&nbsp;<strong>ch&egrave; bưởi</strong>&nbsp;ra b&aacute;t, cho l&ecirc;n b&ecirc;n tr&ecirc;n mọt &iacute;t nước cốt dừa v&agrave; đậu phộng gi&atilde; nhỏ l&agrave; c&oacute; thể thưởng thức rồi.</li>\r\n	<li>Ăn lạnh: M&uacute;c ch&egrave; bưởi ra &frac12; ly lớn, cho phần đ&aacute; b&agrave;o l&ecirc;n tr&ecirc;n, tiếp đ&oacute; bạn cho nước cốt dừa, đậu phộng gi&atilde; nhỏ l&ecirc;n ph&iacute;a tr&ecirc;n đ&aacute; b&agrave;o v&agrave; &frac12; &ndash; 1 th&igrave;a nước cốt chanh t&ugrave;y th&iacute;ch l&agrave; c&oacute; thể thưởng thức rồi nh&eacute;.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 53000, 1462172438, 1462172438, 0, NULL, 21),
(15, 5, 'Chè ngô', 'che_ngo.jpg', 'Có nhiều món ăn ngon nhưng không hề khó làm và chè bắp (ngô) là một trong số đó', '<p><strong>Ch&egrave; ng&ocirc;</strong>&nbsp;l&agrave; m&oacute;n ăn kh&ocirc;ng thể thiếu trong m&ugrave;a h&egrave;. Bởi kh&ocirc;ng chỉ gi&uacute;p giải nhiệt&nbsp;m&agrave; c&ograve;n c&oacute; h&agrave;m lượng dinh dưỡng cao. H&atilde;y c&ugrave;ng v&agrave;o bếp với&nbsp;<a href="http://monan9.com/" target="_blank" title="Món ăn ngon"><strong>monan9.com</strong></a>&nbsp;v&agrave; học&nbsp;<strong>c&aacute;ch nấu ch&egrave; ng&ocirc; ngon ngọt, dẻo thơm</strong>&nbsp;giải nhiệt m&ugrave;a h&egrave; nh&eacute;!</p>\r\n\r\n<h2>C&aacute;ch nấu ch&egrave; ng&ocirc; ngon</h2>\r\n\r\n<h3>Nguy&ecirc;n liệu nấu ch&egrave; ng&ocirc; gồm c&oacute;:</h3>\r\n\r\n<ul>\r\n	<li>Bắp ng&ocirc; tươi: 4 bắp;</li>\r\n	<li>Nước cốt dừa: 1 hộp;</li>\r\n	<li>Bột ng&ocirc; hoặc bột sắn d&acirc;y: 2 th&igrave;a cafe;</li>\r\n	<li>L&aacute; nếp: 3 l&aacute;;</li>\r\n	<li>Đường, muối, vừng rang.</li>\r\n</ul>\r\n\r\n<h3>C&aacute;c bước thực hiện c&aacute;ch nấu ch&egrave; ng&ocirc; ngon</h3>\r\n\r\n<p><img alt="Cách nấu chè ngô ngon ngọt, dẻo bùi" src="http://monan9.com/wp-content/uploads/2015/04/cach-nau-che-ngo-ngon1.jpg" style="height:451px; width:600px" /></p>\r\n\r\n<p>&ndash; Ng&ocirc; lột vỏ, rửa sạch v&agrave; đem luộc ch&iacute;n. Sau khi ch&iacute;n vớt ra để nguội, r&aacute;o nước v&agrave; d&ugrave;ng dao t&aacute;ch lấy phần hạt, bỏ l&otilde;i. Khi luộc cho th&ecirc;m 1 th&igrave;a đường để ng&ocirc; được ngọt hơn nh&eacute;.</p>\r\n\r\n<p>&ndash; Đun lại nồi nước luộc ng&ocirc; v&agrave; cho l&aacute; nếp v&agrave;o. Đun s&ocirc;i một lần nữa. Đ&acirc;y ch&iacute;nh l&agrave;&nbsp;<strong>c&aacute;ch nấu ch&egrave; ng&ocirc; ngon</strong>&nbsp;ngọt v&agrave; thơm nức mũi đấy bạn ạ.</p>\r\n\r\n<p>&ndash; Trong khi chờ nước s&ocirc;i th&igrave; h&ograve;a hỗn hợp bột ng&ocirc; v&agrave; bột sắn d&acirc;y theo tỷ lệ 1:3. Khi nước s&ocirc;i th&igrave; vớt bỏ l&aacute; nếp v&agrave; cho hỗn hợp bột n&agrave;y v&agrave;o, khuấy nhanh tay để bột kh&ocirc;ng bị d&iacute;nh v&agrave; v&oacute;n cục. Hỗn hợp bột n&agrave;y ch&iacute;nh l&agrave; điểm nhấn để&nbsp;<strong>c&aacute;ch nấu ch&egrave; ng&ocirc; ngon</strong>&nbsp;v&agrave; dẻo đ&oacute;.</p>\r\n\r\n<p>&ndash; Khi bột ch&iacute;n v&agrave; s&aacute;nh mịn th&igrave; cho ng&ocirc; v&agrave;o. Vừa cho vừa khuấy đều tay bạn nh&eacute;. Khi ng&ocirc; ch&iacute;n mềm th&igrave; cho th&ecirc;m đường để ch&egrave; c&oacute; vị ngọt v&agrave; v&agrave;i hạt muối để ch&egrave; ng&ocirc; ngon v&agrave; đậm đ&agrave;.</p>\r\n\r\n<h3>Hướng dẫn ăn v&agrave; y&ecirc;u cầu th&agrave;nh phẩm</h3>\r\n\r\n<p><img alt="Cách nấu chè ngô ngon không cưỡng nổi" src="http://monan9.com/wp-content/uploads/2015/04/cach-nau-che-ngo-ngon5.jpg" style="height:449px; width:600px" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&ndash; Bạn m&uacute;c ch&egrave; ng&ocirc; ra từng b&aacute;t, cho th&ecirc;m nước cốt dừa, vừng rang v&agrave; dừa nạo sợi l&ecirc;n tr&ecirc;n l&agrave; c&oacute; thể thưởng thức được rồi. Ch&egrave; ng&ocirc; c&oacute; thể ăn n&oacute;ng hoặc cho th&ecirc;m ch&uacute;t đ&aacute; b&agrave;o, thạch rau c&acirc;u đều ngon bạn nh&eacute;.</p>\r\n\r\n<p>&ndash; Hạt ng&ocirc; phải ch&iacute;n mềm, nước ch&egrave; phải s&aacute;nh mịn, kh&ocirc;ng v&oacute;n cục v&agrave; kh&ocirc;ng qu&aacute; lo&atilde;ng hay qu&aacute; đặc.</p>\r\n\r\n<p>&ndash; Ch&egrave; c&oacute; vị ngọt thanh, đậm đ&agrave;, v&agrave; thơm.</p>\r\n\r\n<p>Nếu bạn vẫn chưa h&agrave;i l&ograve;ng với c&aacute;ch nấu ch&egrave; ng&ocirc; tr&ecirc;n đ&acirc;y, mời bạn tham khảo một kinh nghiệm:&nbsp;<a href="http://cachlam9.com/cach-nau-che-ngo-cuc-ngon-cach-nau-che-bap.html" target="_blank" title="Cách nấu chè ngô ngon"><strong>Nấu ch&egrave; ng&ocirc;</strong></a>&nbsp;kh&aacute;c từ Cachlam9.Com.</p>\r\n', 32000, 1462172581, 1462172581, 0, NULL, 26),
(16, 3, 'Cá kho tộ', 'ca_kho_to.jpg', 'Món cá kho tộ ăn với cơm nóng, thêm một đĩa rau luộc và nước canh.', '<h2>C&aacute;ch l&agrave;m c&aacute; kho tộ</h2>\r\n\r\n<p><img alt="Cách làm cá kho tộ ngon miễn chê" src="http://monan9.com/wp-content/uploads/2015/04/cach-lam-ca-kho-to-cuc-ngon-it-ai-biet.jpg" style="height:399px; width:500px" /></p>\r\n\r\n<h3>Nguy&ecirc;n liệu cần c&oacute; cho m&oacute;n c&aacute; kho tộ ngon đừng hỏi</h3>\r\n\r\n<ul>\r\n	<li>C&aacute;: 1 con c&aacute; chừng 1kg. Với m&oacute;n c&aacute; kho tộ c&oacute; thể nấu c&aacute; l&oacute;c, c&aacute; basa hay c&aacute; r&ocirc; đều được. T&ugrave;y v&agrave;o sở th&iacute;ch của mỗi gia đ&igrave;nh hoặc c&oacute; thể đổi m&oacute;n bằng c&aacute;c loại c&aacute; kể tr&ecirc;n.</li>\r\n	<li>Thịt: khoảng 2g, lưu &yacute; chọn thịt ba chỉ kho sẽ ngon hơn (kh&ocirc;ng n&ecirc;n chọn thịt nạc nguy&ecirc;n hoặc nhiều mỡ).</li>\r\n	<li>Dừa: 1 quả bổ lấy nước.</li>\r\n	<li>C&aacute;c loại gia vị: nước h&agrave;ng, nước mắt, hạt n&ecirc;m v&agrave; tỏi.</li>\r\n</ul>\r\n\r\n<h3><strong>C&aacute;c bước nấu m&oacute;n c&aacute; kho tộ.</strong></h3>\r\n\r\n<p><img alt="Cách làm cá kho tộ ngon đúng điệu tại nhà" src="http://monan9.com/wp-content/uploads/2015/04/cach-lam-ca-kho-to-cuc-ngon-it-ai-biet1.jpg" style="height:375px; width:500px" /></p>\r\n\r\n<ul>\r\n	<li>Bước 1: C&aacute; l&agrave;m sạch,&nbsp;cắt kh&uacute;c vừa miệng ăn v&agrave; để r&aacute;o nước.&nbsp;C&aacute; sau khi l&agrave;m sạch, cắt kh&uacute;c cho gia vị v&agrave;o cho ngấm. Lần lượt n&ecirc;m 2 đến 3 th&igrave;a c&agrave; ph&ecirc; nước h&agrave;ng, th&ecirc;m ch&uacute;t nước mắt, hạt n&ecirc;m v&agrave; ướp trong v&ograve;ng nửa tiếng.</li>\r\n	<li>Bước 2: Thịt ba chỉ rửa sạch th&aacute;i vừa miệng ăn v&agrave; để ri&ecirc;ng v&agrave;o b&aacute;t nhỏ.</li>\r\n	<li>Bước 3: Tỏi b&oacute;c sạch vỏ v&agrave; đập dập. Chảo bắc l&ecirc;n bếp cho &iacute;t dầu ăn v&agrave; phi tỏi cho thơm. Sau đ&oacute;, đổ thịt ba chỉ đ&atilde; chuẩn bị trước đảo đều tay thịt hơi x&eacute;m th&igrave; tắt bếp.</li>\r\n	<li>Bước 4: L&uacute;c n&agrave;y c&aacute; đ&atilde; được ướp gia vị xếp từng kh&uacute;c v&agrave;o nồi. Lần lượt theo thứ tự c&aacute; ở tr&ecirc;n, thịt ở dưới. Lưu &yacute;, phần nước c&aacute; ướp đổ cả v&agrave;o nồi. Cuối c&ugrave;ng đừng qu&ecirc;n đổ nước cốt dừa đ&atilde; chuẩn bị để kho c&aacute;. So với việc kho c&aacute; với nước s&ocirc;i để nguội sử dụng nước cốt dừa gi&uacute;p&nbsp;<strong>c&aacute;ch l&agrave;m c&aacute; kho tộ</strong>&nbsp;c&oacute; hương vị thơm ngon, b&eacute;o ngậy v&agrave;&nbsp;hấp dẫn hơn.</li>\r\n	<li>Bước 5: Bật nhỏ lửa đun. Trong qu&aacute; tr&igrave;nh nấu c&oacute; thể nếm để vừa miệng ăn. Khi gần ch&iacute;n c&oacute; thể th&ecirc;m ch&uacute;t ớt, ti&ecirc;u, h&agrave;nh trang tr&iacute; ở b&ecirc;n tr&ecirc;n v&agrave; gi&uacute;p m&oacute;n c&aacute; &aacute;t đi vị tanh.</li>\r\n</ul>\r\n\r\n<p><strong>Gợi &yacute; thực đơn ăn với m&oacute;n c&aacute; kho tộ.</strong></p>\r\n\r\n<p><strong>M&oacute;n c&aacute; kho tộ</strong>&nbsp;ăn với cơm n&oacute;ng, th&ecirc;m một đĩa rau luộc v&agrave; nước canh. Như vậy, bữa ăn gia đ&igrave;nh được chế biến kh&aacute; nhanh nhưng đảm bảo th&agrave;nh vi&ecirc;n n&agrave;o trong nh&agrave; ai cũng th&iacute;ch m&oacute;n ăn n&agrave;y.<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; hướng dẫn&nbsp;<strong>c&aacute;ch l&agrave;m c&aacute; kho tộ</strong>&nbsp;đơn giảm với 5 bước. Chỉ cần thực hiện theo c&aacute;c bước đ&oacute;, d&ugrave; l&agrave; người chưa nấu ăn bao giờ cũng c&oacute; thể chế biến ngon chi&ecirc;u đ&atilde;i cả nh&agrave; v&agrave;o dịp cuối tuần.</p>\r\n\r\n<p>Ngo&agrave;i ra bạn c&oacute; thể thay đổi m&oacute;n c&aacute; kho bằng c&aacute;ch chế biến c&aacute; kiểu kh&aacute;c gi&uacute;p gia đ&igrave;nh được đổi vị thường xuy&ecirc;n, ăn ngon miệng hơn:&nbsp;<strong><a href="http://cachlam9.com/cach-lam-ca-ngu-rim-toi-ot-tuyet-ngon-va-la-mieng.html" target="_blank">C&aacute;ch l&agrave;m c&aacute; hấp bia</a></strong>&nbsp;hay&nbsp;<strong><a href="http://cachlam9.com/cach-lam-ca-ngu-rim-toi-ot-tuyet-ngon-va-la-mieng.html" target="_blank">c&aacute;ch l&agrave;m cứ ngừ rim tỏi ớt</a></strong>&nbsp;đều cũng rất ngon, hấp dẫn kh&ocirc;ng k&eacute;m g&igrave; những m&oacute;n c&aacute; kho bạn nh&eacute;.</p>\r\n', 58000, 1462172673, 1462172673, 0, NULL, 26);
INSERT INTO `food` (`id_food`, `id_type`, `name_food`, `image_food`, `mini_content`, `content`, `unit_price`, `time_add`, `time_edit`, `today`, `author`, `cook`) VALUES
(17, 1, 'Bún thịt nướng', 'bun_thit_nuong.jpg', 'Nếu bạn ướp thịt ngon thì sẽ có một tô bún ngon tuyệt.', '<h2>L&agrave;m m&oacute;n b&uacute;n thịt nướng thơm &#39;nức mũi&#39; theo nguy&ecirc;n li&ecirc;̣u sẵn có</h2>\r\n\r\n<p><strong>C&oacute; rất nhiều c&aacute;ch l&agrave;m b&uacute;n thịt nướng ngon v&agrave; quan trọng nhất ch&iacute;nh l&agrave; kh&acirc;u ướp thịt. Nếu bạn ướp thịt ngon th&igrave; sẽ c&oacute; một t&ocirc; b&uacute;n ngon tuyệt.</strong></p>\r\n\r\n<p>C&oacute; rất nhiều c&aacute;ch l&agrave;m b&uacute;n thịt nướng ngon v&agrave; quan trọng nhất ch&iacute;nh l&agrave; kh&acirc;u ướp thịt. Nếu bạn ướp thịt ngon th&igrave; sẽ c&oacute; một t&ocirc; b&uacute;n ngon tuyệt.</p>\r\n\r\n<p>Cuối tuần l&agrave; dịp ph&ugrave; hợp để gia đ&igrave;nh qu&acirc;y quần c&ugrave;ng nhau ngồi ăn bữa cơm ấm c&uacute;ng. V&agrave; m&oacute;n b&uacute;n thịt nướng l&agrave; một trong những m&oacute;n ăn ph&ugrave; hợp nhất. Một t&ocirc; b&uacute;n thịt nướng thơm ngon với đầy đủ c&aacute;c loại gia vị kh&aacute;c nhau, thịt xi&ecirc;n nướng mềm dậy thơm m&ugrave;i, ăn k&egrave;m rau sống v&agrave; đồ chua rất th&iacute;ch hợp cho những bữa ăn gia đ&igrave;nh v&agrave;o dịp cuối tuần.</p>\r\n\r\n<p><strong>Nguy&ecirc;n liệu:</strong></p>\r\n\r\n<p>1,8kg thịt heo m&ocirc;ng hay thăn đầy, th&aacute;i l&aacute;t mỏng (c&oacute; độ d&agrave;y khoảng 0,7cm) 1/2 muỗng canh bột hạt ti&ecirc;u đen</p>\r\n\r\n<p>3 củ h&agrave;nh kh&ocirc;, băm nhỏ 1 muỗng c&agrave; ph&ecirc; bột ngọt (t&ugrave;y chọn)</p>\r\n\r\n<p>5 củ tỏi, băm nhỏ 2 muỗng canh mật ong</p>\r\n\r\n<p>&frac12; ch&eacute;n sả, băm nhỏ 3 muỗng canh nước mắm</p>\r\n\r\n<p>3 muỗng canh hạt vừng trắng 1 muỗng canh dầu m&egrave; (vừng)</p>\r\n\r\n<p>&frac12; muỗng canh đường</p>\r\n\r\n<p>1/2 muỗng canh dầu thực vật</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>1 muỗng canh muối</p>\r\n\r\n<p><strong>C&aacute;ch l&agrave;m m&oacute;n b&uacute;n thịt nướng ngon theo c&ocirc;ng thức gia truyền:</strong></p>\r\n\r\n<p>Bước 1: Bạn cho c&aacute;c nguy&ecirc;n liệu từ h&agrave;nh kh&ocirc; băm nhỏ&hellip;đến dầu thực vật v&agrave; một t&ocirc; lớn, trộn đều l&ecirc;n. Tiếp đ&oacute; bạn cho thịt heo đ&atilde; th&aacute;i l&aacute;t v&agrave;o chung, bạn d&ugrave;ng tay xoa b&oacute;p để gia vị ngấm đều v&agrave;o thịt, sau đ&oacute; đem để trong tủ lạnh 24h.</p>\r\n\r\n<table align="center" cellpadding="0" cellspacing="0" style="width:400px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140604-1.jpg" title=""><img alt="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" src="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140604-1.jpg" title="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bước 2: Sau khi ướp thịt bạn xi&ecirc;n thịt v&agrave; những que xi&ecirc;n, rồi đem nướng tr&ecirc;n than hồng hoặc bếp khoảng 20 ph&uacute;t. Trong khi nướng ch&uacute; &yacute; xoay thịt li&ecirc;n tục để tr&aacute;nh bị ch&aacute;y.</p>\r\n\r\n<table align="center" cellpadding="0" cellspacing="0" style="width:400px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140604-2.jpg" title=""><img alt="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" src="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140604-2.jpg" title="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bước 3: Bạn c&oacute; thể cho những xi&ecirc;n thịt v&agrave;o vỉ nướng rồi kẹp chặt vỉ lại, để thịt kh&ocirc;ng bị d&iacute;nh trước khi nướng bạn phết một &iacute;t dầu l&ecirc;n hai mặt của vỉ. Đem thịt nướng tr&ecirc;n than hồng c&oacute; nhiệt độ vừa phải, li&ecirc;n tục lật thịt đều 2 mặt để thịt kh&ocirc;ng bị ch&aacute;y v&agrave; kh&ocirc;.</p>\r\n\r\n<table align="center" cellpadding="0" cellspacing="0" style="width:400px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140604-3.jpg" title=""><img alt="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" src="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140604-3.jpg" title="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bước 4: Khi thịt ch&iacute;n bạn dọn ra ăn c&ugrave;ng với b&uacute;n v&agrave; c&aacute;c loại rau.</p>\r\n\r\n<table align="center" cellpadding="0" cellspacing="0" style="width:400px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140653-4.jpg" title=""><img alt="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" src="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140653-4.jpg" title="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bước 5: Bạn pha nước mắm chua ngọt với tỷ lệ: 1 nước mắm, 1 đường, 1 nước lọc v&agrave; 4 chanh. Gi&atilde; nhuyễn ớt v&agrave; tỏi rồi cho v&agrave;o c&ugrave;ng. N&ecirc;m nếm lại cho vừa miệng.</p>\r\n\r\n<table align="center" cellpadding="0" cellspacing="0" style="width:400px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140653-5.jpg" title=""><img alt="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" src="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140653-5.jpg" title="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bước 6: Rau sống ăn k&egrave;m: &iacute;t rau thơm, dưa leo, x&agrave; l&aacute;ch&hellip; (c&aacute;c loạị rau sống nếu bạn th&iacute;ch), th&ecirc;m một &iacute;t lạc rang gi&atilde; nhỏ, h&agrave;nh kh&ocirc;&hellip;</p>\r\n\r\n<table align="center" cellpadding="0" cellspacing="0" style="width:400px">\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140653-6.jpg" title=""><img alt="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" src="http://imgs.vietnamnet.vn/Images/2015/11/23/14/20151123140653-6.jpg" title="nguyên liệu sẵn có, ướp thịt, bún thịt nướng, Cách làm" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đ&acirc;y l&agrave; một trong những c&aacute;ch l&agrave;m m&oacute;n b&uacute;n thịt nướng ngon tuyệt. Nếu bạn muốn l&agrave;m một m&oacute;n để cả gia đ&igrave;nh qu&acirc;y quần dịp cuối tuần th&igrave; c&ograve;n chờ g&igrave; nữa m&agrave; kh&ocirc;ng chia sẻ link n&agrave;y l&ecirc;n tường Facebook để nhớ c&aacute;ch l&agrave;m.</p>\r\n', 16000, 1462174302, 1462174302, 0, NULL, 21),
(18, 3, 'Chả cá', 'cha_ca.jpg', 'Chả cá là một loại chả được làm từ thịt của các loại cá ăn được.', '<p>Thời tiết chuyển m&ugrave;a oi bức v&agrave; hanh kh&ocirc; khiến cả gia đ&igrave;nh bạn cảm thấy kh&oacute; chịu, mệt mỏi v&agrave; ch&aacute;n ăn phải kh&ocirc;ng. L&agrave; người nội trợ đảm đang cho gia đ&igrave;nh, thay v&igrave; chế biến những m&oacute;n ăn quen thuộc, bạn h&atilde; đầu y đầu tư một ch&uacute;t &iacute;t thời gian, c&ocirc;ng sức để chến biến n&ecirc;n những m&oacute;n ăn ngon với hương vị mới lạ để đổi khẩu vị cho cả gia đ&igrave;nh, gi&uacute;p mọi người ăn ngon hơn, hấp thu chất dinh dưỡng tốt hơn v&agrave; khỏe hơn để chống lại thời tiết giao m&ugrave;a kh&oacute; chịu n&agrave;y nh&eacute;.&nbsp;<strong>C&aacute;ch l&agrave;m m&oacute;n chả c&aacute; thơm ngon đơn giản tại nh&agrave;</strong>&nbsp;m&agrave; Cachlam9.com hướng dẫn sau đ&acirc;y sẽ l&agrave; một sự lựa chọn ph&ugrave; hợp cho bạn đấy.</p>\r\n\r\n<h2>C&aacute;ch l&agrave;m chả c&aacute;</h2>\r\n\r\n<h3>Nguy&ecirc;n liệu l&agrave;m chả c&aacute;</h3>\r\n\r\n<ul>\r\n	<li>C&aacute; tươi: 600g (Bạn n&ecirc;n chọn c&aacute; ph&aacute;t l&aacute;t hoặc c&aacute; thu khi l&agrave;m chả sẽ ngon hơn nh&eacute;, bạn cũng c&oacute; thể sử dụng g&oacute;i bột c&aacute; l&agrave;m sẵn để tiết kiệm thời gian chế biến nhưng hương vị sẽ kh&ocirc;ng ngon bằng c&aacute; tươi đ&acirc;u);</li>\r\n	<li>L&aacute; chanh non: 1 nắm nhỏ;</li>\r\n	<li>Trứng g&agrave;: 2 quả;</li>\r\n	<li>H&agrave;nh kh&ocirc;, tỏi: 50g;</li>\r\n	<li>H&agrave;nh l&aacute;, ng&ograve; r&iacute;: 50g;</li>\r\n	<li>Ớt sừng: 3 tr&aacute;i;</li>\r\n	<li>Chanh: 1 t&aacute;i;</li>\r\n	<li>Gia vị: Hạt n&ecirc;m, bột ngọt, nước mắm, ti&ecirc;u, ớt bột, muối, dầu ăn;</li>\r\n	<li>Rau sống ăn k&egrave;m: X&agrave; l&aacute;ch, rau thơm, gi&aacute; đỗ, 1 tr&aacute;i dưa leo.</li>\r\n</ul>\r\n\r\n<h3>Sơ chế nguy&ecirc;n liệu</h3>\r\n\r\n<p><img alt="Cách làm chả cá thơm ngon đơn giản tại nhà, cach lam cha ca thom ngon don gian tai nha" src="http://cachlam9.com/wp-content/uploads/2013/08/Cach-lam-cha-ca-thom-ngon-don-gian-tai-nha.jpg" style="height:326px; width:400px" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>C&aacute; tươi: Bạn l&agrave;m sạch, rửa qua với nước muối lo&atilde;ng, để r&aacute;o. Sau đ&oacute; bạn d&ugrave;ng dao, lạng lất thịt, bỏ phần xương, da v&agrave; đầu, bạn n&ecirc;n lọc xương cho kỹ để an to&agrave;n khi cho c&aacute;c b&eacute; y&ecirc;u ăn nh&eacute;. Ướp c&aacute; với 1 th&igrave;a hạt n&ecirc;m, &frac12; th&igrave;a bột ngọt, &frac12; th&igrave;a ti&ecirc;u, 2 th&igrave;a h&agrave;nh tỏi băm nhuyễn, &frac12; th&igrave;a nước mắm, &frac12; th&igrave;a dầu ăn để trong 30 ph&uacute;t cho ngấm đều gia vị;</li>\r\n	<li>H&agrave;nh kh&ocirc;, tỏi: L&agrave;m sạch, băm nhuyễn;</li>\r\n	<li>Ớt tr&aacute;i: Bỏ hạt, băm nhỏ;</li>\r\n	<li>L&aacute; chanh: rửa sạch, th&aacute;i sợi chỉ;</li>\r\n	<li>H&agrave;nh l&aacute;, ng&ograve; r&iacute;: L&agrave;m sạch, h&agrave;nh l&aacute; th&aacute;i nhuyễn, ng&ograve; r&iacute; cắt kh&uacute;c 3cm;</li>\r\n	<li>Rau sống ăn k&egrave;m: Bạn nhặt, rửa sạch từng loại, để r&aacute;o nước, ri&ecirc;ng dưa leo bạn ch&agrave; kỹ mủ hai đầu v&agrave; cắt th&agrave;nh từng l&aacute;t d&agrave;i 3cm. Trộn c&aacute;c loại rau sống lại với nhau, b&agrave;y ra đĩa rồi xếp dưa leo l&ecirc;n tr&ecirc;n;</li>\r\n	<li>L&agrave;m nước mắm chua ngọt ăn k&egrave;m: Bạn trộn1 th&igrave;a ớt, 1 th&igrave;a tỏi với 1,5 th&igrave;a đường, d&ugrave;ng ch&agrave;y gi&atilde; thật nhuyễn để khi ho&agrave;n th&agrave;nh nước mắm sẽ c&oacute; độ sền sệt hấp dẫn. Sau đ&oacute; bạn cho th&ecirc;m 3 th&igrave;a nước mắm, 1 th&igrave;a nước cốt chanh khuấy tan đều, chp v&agrave;o ch&eacute;n nhỏ. Bạn c&oacute; thể gia giảm lượng đường t&ugrave;y theo khẩu vị của gia đ&igrave;nh nh&eacute;.</li>\r\n</ul>\r\n\r\n<h3>Thực hiện l&agrave;m m&oacute;n chả c&aacute;</h3>\r\n\r\n<ul>\r\n	<li>Bạn cho c&aacute; đ&atilde; ướp gia vị v&agrave;o m&aacute;y xay, đập v&agrave;o đ&oacute; th&ecirc;m 2 quả trứng, l&aacute; chanh, h&agrave;nh l&aacute; đ&atilde; th&aacute;i mịn xay thật nhuyễn, cho ra đĩa;</li>\r\n	<li>Bạn đeo bao tay v&agrave;o, thấm &iacute;t dầu ăn tr&ecirc;n tay để chả c&aacute; kh&ocirc;ng bị d&iacute;nh v&agrave; vi&ecirc;n chả c&aacute; lại th&agrave;nh từng miếng nhỏ vừa ăn. L&uacute;c n&agrave;y bạn c&oacute; thể thoải m&aacute;i tạo đủ kiều h&igrave;nh ngộ ngĩnh với chả c&aacute; đấy;</li>\r\n	<li>Cho một lượng dầu vừa đủ ngập mặt chả c&aacute; v&agrave;o chảo, để dầu n&oacute;ng tr&ecirc;n bếp, vặn lửa nhỏ v&agrave; cho từng miếng chả c&aacute; v&agrave;o chi&ecirc;n v&agrave;ng đều. Bạn n&ecirc;n chi&ecirc;n với lửa nhỏ th&ocirc;i nh&eacute; kẻo chả c&aacute; rất dễ ch&aacute;y;</li>\r\n	<li>Vớt ra rổ c&oacute; giấy thấm dầu rồi b&agrave;y ra đĩa, rắc ng&ograve; r&iacute; l&ecirc;n tr&ecirc;n để m&oacute;n ăn th&ecirc;m phần đẹp mắt l&agrave; bạn đ&atilde; ho&agrave;n th&agrave;nh m&oacute;n chả c&aacute; thơm ngon, hấp dẫn rồi đấy.</li>\r\n</ul>\r\n\r\n<p><strong>Y&ecirc;u cầu m&oacute;n ăn v&agrave; c&aacute;ch ăn</strong></p>\r\n\r\n<ul>\r\n	<li>M&oacute;n chả c&aacute; c&oacute; m&agrave;u v&agrave;ng đều, c&oacute; m&ugrave;i thơm hấp dẫn, vị vừa ăn;</li>\r\n	<li>Khi ăn, bạn sẽ cảm nhận được vị tươi ngon của &aacute;c h&ograve;a quyện với c&aacute;c gia vị kh&aacute;c tạo n&ecirc;n một m&oacute;n ăn rất hấp dẫn v&agrave; lạ miệng;</li>\r\n	<li>Bạn n&ecirc;n ăn khi chả c&aacute; c&ograve;n n&oacute;ng, d&ugrave;ng k&egrave;m với nước mắm chua ngọt v&agrave; rau sống nh&eacute;, sẽ rất thơm ngon đấy.</li>\r\n</ul>\r\n\r\n<p>Để th&ecirc;m nhiều lựa chọn hơn cho việc nội trợ bạn c&oacute; thể tham khảo th&ecirc;m một số m&oacute;n ăn ngon từ c&aacute; như:</p>\r\n\r\n<ul>\r\n	<li><a href="http://monan9.com/cach-nau-canh-ca-ngon-tuyet-dam-da-huong-vi-que.html" target="_blank">C&aacute;ch nấu canh c&aacute; ngon</a></li>\r\n	<li><a href="http://monan9.com/cach-lam-ca-kho-to-cuc-ngon-it-ai-biet.html" target="_blank">C&aacute;ch l&agrave;m c&aacute; kho tộ</a></li>\r\n</ul>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave;&nbsp;<strong>C&aacute;ch l&agrave;m m&oacute;n chả c&aacute; thơm ngon đơn giản tại nh&agrave;,&nbsp;</strong>tuy c&oacute; mất kh&aacute; nhiều thời gian trong kh&acirc;u chế biến nhưng bạn sẽ c&oacute; được một m&oacute;n ăn thơm ngon, hấp dẫn, lạ miệng, đảm bảo dinh dưỡng v&agrave; an to&agrave;n vệ sinh thực phẩm để đổi khẩu vị v&agrave; chăm s&oacute;c sức khỏe cho cả gia đ&igrave;nh đấy. Ch&uacute;c bạn chế biến th&agrave;nh c&ocirc;ng v&agrave; ăn ngon miệng nh&eacute;.</p>\r\n', 31000, 1462179578, 1462179578, 0, NULL, 26),
(19, 2, 'Rau muống', 'rau_muong1462253389.jpg', 'Rau muống có chứa hàm lượng canxi cao tốt cho những người bị loãng xương, huyết áp thấp và phụ nữ có thai', '<p><strong>Những lợi &iacute;ch tuyệt vời của rau muống:</strong></p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><a href="http://images.vov.vn/uploaded/thuthuy/2015_07_26/rau_muong_NOGO.jpg" title=""><img alt="nhung loi ich tuyet voi tu rau muong hinh 0" src="http://images.vov.vn/w490/uploaded/thuthuy/2015_07_26/rau_muong_NOGO.jpg" title="những lợi ích tuyệt vời từ rau muống hình 0" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Rau muống l&agrave; m&oacute;n ăn gần gũi với con người. Đ&acirc;y l&agrave; loại&nbsp;rau&nbsp;rất bổ dưỡng bởi n&oacute; chứa nhiều vitamin v&agrave; kho&aacute;ng chất.</p>\r\n\r\n<p>Rau muống c&oacute; chứa h&agrave;m lượng canxi cao tốt cho những người bị lo&atilde;ng xương v&agrave; huyết &aacute;p thấp v&agrave; phụ nữ c&oacute; thai. Ngo&agrave;i ra, trong rau muống c&ograve;n chứa protit, glucid, cellulose, vitamin B1, B2&hellip; Những người bị t&aacute;o b&oacute;n ăn rau muống cũng rất tốt. Trong ngọn rau muống, c&oacute; một chất giống như&nbsp;insulin&nbsp;n&ecirc;n những người bị&nbsp;đ&aacute;i th&aacute;o đường&nbsp;c&oacute; thể ăn 5 &ndash; 10 ngọn rau muống trước bữa ăn khoảng 30 ph&uacute;t.</p>\r\n\r\n<p>Những người cao tuổi ăn rau muống ng&agrave;y hai bữa c&oacute; n&atilde;o trẻ hơn 5 năm v&agrave; &iacute;t bị suy giảm tinh thần hơn 40% so với những người ăn &iacute;t rau muống.</p>\r\n\r\n<p>-<strong><em>Thanh nhiệt giải độc</em></strong>: Ăn rau muống luộc tốt cho b&agrave; mẹ c&oacute; thai thiếu sắt, b&agrave; mẹ đang cho con b&uacute;, người bị t&aacute;o b&oacute;n, tiểu đục, tăng huyết &aacute;p, nhịp tim nhanh, trẻ c&ograve;i xương (lấy nước rau muống quấy bột).<br />\r\n-&nbsp;<strong><em>Chứng kiết lỵ</em></strong>: Thường gặp m&ugrave;a h&egrave; thu (ban đầu bị ti&ecirc;u chảy do thấp nhiệt sau chuyển sang kiết lỵ, ph&acirc;n c&oacute; nhầy m&agrave;u đỏ trắng, đau thắt bụng): lấy một b&oacute; rau muống tươi nhặt rửa sạch, th&ecirc;m một &iacute;t vỏ qu&yacute;t kh&ocirc; để l&acirc;u (trần b&igrave;) nấu với nhiều nước, lửa nhỏ trong v&agrave;i giờ rồi uống.<br />\r\n-&nbsp;<strong><em>Đau dạ d&agrave;y, n&oacute;ng ruột, ợ chua, miệng kh&ocirc; đắng</em></strong>: Rau muống 20g, rau m&aacute; 20g, rau sam 16g, cỏ mực 20g, vỏ qu&yacute;t kh&ocirc; 12g. Tất cả sao qua cho v&agrave;o ấm, đổ 500ml nước, sắc c&ograve;n 250ml chia 2 lần uống l&uacute;c đ&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>Say sắn, ngộ độc sắn:</strong></em>&nbsp;một nắm rau muống nhặt rửa sạch, gi&atilde; n&aacute;t vắt lấy nước uống. Hoặc lấy 100g rau muống l&agrave;m sạch cắt đoạn, c&aacute;m gạo tẻ 50g trộn v&agrave;o gi&atilde; nhuyễn, th&ecirc;m nước cho lo&atilde;ng để uống.<br />\r\n-&nbsp;<strong><em>Chảy m&aacute;u cam, ho n&ocirc;n ra m&aacute;u, đại tiểu tiện ra m&aacute;u, trĩ, lỵ ra m&aacute;u...</em></strong>: Gi&atilde; rau muống uống nước cốt hoặc th&ecirc;m đường, mật ong.<br />\r\n-&nbsp;<strong><em>Kh&iacute; hư bạch đới</em></strong>: Rau muống cả rễ 500g, hoa r&acirc;m bụt trắng 250g hầm với thịt lợn hoặc thịt g&agrave;, ăn thịt uống nước.<br />\r\n-&nbsp;<strong><em>Đ&aacute;i th&aacute;o đường</em></strong>: Rau muống 60g, r&acirc;u ng&ocirc; 30g nấu nước uống (rau muống t&iacute;a tốt hơn rau muống trắng).<br />\r\n-<strong><em>&nbsp;Quai bị</em></strong>: Rau muống 200 - 400g luộc kỹ, ăn cả c&aacute;i lẫn nước. C&oacute; thể pha đường v&agrave;o nước rau.<br />\r\n-&nbsp;<strong><em>Lở ngứa, lo&eacute;t ngo&agrave;i da, zona</em></strong>: Ngọn rau muống v&agrave; l&aacute; c&acirc;y v&ograve;i voi rửa sạch gi&atilde; nhuyễn với &iacute;t muối đắp l&ecirc;n.<br />\r\n-&nbsp;<strong><em>R&ocirc;m sảy mẩn ngứa, sởi, thủy đậu ở trẻ em</em></strong>: D&ugrave;ng nước nấu rau muống xoa, rửa, tắm. Rau muống một b&oacute; rửa sạch, th&aacute;i nhỏ; g&agrave; l&ocirc;ng v&agrave;ng, ch&acirc;n v&agrave;ng, da v&agrave;ng 1 con l&agrave;m sạch mổ moi bỏ l&ograve;ng, nhồi rau muống v&agrave;o, hầm nhừ, th&ecirc;m &iacute;t rượu. Khi ch&iacute;n bỏ rau ăn thịt g&agrave;. Ăn độ 3 con choai choai (g&agrave; gi&ograve;).</p>\r\n\r\n<p><strong>Những người kh&ocirc;ng n&ecirc;n ăn rau muống:</strong></p>\r\n\r\n<p>Rau muống l&agrave; loại rau được nhiều người ưa th&iacute;ch v&igrave; dễ ăn, dễ chế biến th&agrave;nh nhiều m&oacute;n, giải độc, sinh da thịt, nhuận tr&agrave;ng, lợi tiểu. Tuy nhi&ecirc;n, rau muống lại l&agrave; m&oacute;n đặc biệt cần &ldquo;ki&ecirc;ng kỵ&rdquo; đối với những ai đang bị vết thương tr&ecirc;n da bởi ch&uacute;ng k&iacute;ch th&iacute;ch tăng sinh tế b&agrave;o g&acirc;y sẹo lồi tr&ecirc;n da. Hoặc những người bị vi&ecirc;m đau, nhức khớp, bệnh gout (thống phong) v&agrave; c&aacute;c vi&ecirc;m nhiễm đường tiết niệu thận do sỏi, huyết &aacute;p cao hay những người đang điều trị bệnh nội khoa, ngoại khoa.&nbsp;</p>\r\n\r\n<p>Đang uống thuốc Đ&ocirc;ng y, nếu ăn rau muống sẽ l&agrave;m gi&atilde; thuốc, nhất l&agrave; khi trong thuốc c&oacute; vị độc cần thiết để chữa bệnh (độc trị độc) v&agrave; sẽ l&agrave;m giảm hiệu quả điều trị.&nbsp;Nếu trong gia đ&igrave;nh bạn c&oacute; người bị c&aacute;c bệnh n&agrave;y cũng n&ecirc;n hạn chế ăn rau muống.</p>\r\n\r\n<p><strong>Rau muống kị với những sản phẩm như sữa b&ograve;, sữa chua, pho m&aacute;t:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rau muống kị với những sản phẩm như sữa b&ograve;, sữa chua, pho m&aacute;t đều gi&agrave;u h&agrave;m lượng canxi, c&ograve;n rau muống lại chứa một số th&agrave;nh phần h&oacute;a học c&oacute; thể l&agrave;m ảnh hưởng đến việc hấp thụ canxi, do vậy khi ăn c&ugrave;ng l&uacute;c những loại thực phẩm n&agrave;y sẽ kh&ocirc;ng mang lại gi&aacute; trị dinh dưỡng cao nhất cho cơ thể.</p>\r\n\r\n<p>Theo Cục bảo vệ thực vật (Bộ N&ocirc;ng nghiệp v&agrave; ph&aacute;t triển n&ocirc;ng th&ocirc;n) rau muống l&agrave; loại rau dẫn đầu trong nh&oacute;m rau ăn l&aacute; c&oacute; nguy cơ g&acirc;y ngộ độc thực phẩm cao, chứa rất nhiều thuốc k&iacute;ch th&iacute;ch, thuốc trừ s&acirc;u&hellip; Việc sử dụng c&aacute;c loại rau c&oacute; chứa dư lượng thuốc bảo vệ thực vật vượt ngưỡng c&oacute; thể khiến người ti&ecirc;u d&ugrave;ng bị ngộ độc m&atilde;n t&iacute;nh, gi&atilde;n thể miễn dịch, thậm ch&iacute; ảnh hưởng đến hệ thần kinh.</p>\r\n\r\n<p>Nếu ăn rau muống sống, c&aacute;c k&iacute; sing tr&ugrave;ng n&agrave;y sẽ v&agrave;o cơ thể người, b&aacute;m v&agrave;o trong ruột chui qua th&agrave;nh ruột, chui v&agrave;o trong m&aacute;u, m&aacute;u dẫn ch&uacute;ng đến tất cả c&aacute;c bộ phận trong cơ thể... Sau một thời gian, trứng sẽ nở th&agrave;nh giun s&aacute;n nằm ở đ&oacute; v&agrave; g&acirc;y hại cho cơ thể. Thực tế đ&atilde; ghi nhận nhiều trường hợp bị bị bệnh hiểm ngh&egrave;o do nhiễm giun s&aacute;n qua đường ăn uống.</p>\r\n\r\n<p>Nhằm đảm bảo cho sức khỏe v&agrave; duy tr&igrave; m&oacute;n rau nhiều lợi &iacute;ch trong c&aacute;c bữa cơm gia đ&igrave;nh, ch&uacute;ng ta cần đảm bảo vệ sinh bằng c&aacute;ch rửa sạch từng ngọn, ng&acirc;m nước muối lo&atilde;ng hoặc tốt nhất l&agrave; rửa sạch sau đ&oacute; để r&aacute;o nước cho v&agrave;o t&uacute;i bảo quản trong tủ lạnh v&agrave;i ng&agrave;y mới ăn, như vậy nếu c&oacute; lượng thuốc sẽ bị ph&acirc;n hủy bớt./.</p>\r\n', 17000, 1462253389, 1462253389, 0, NULL, 1),
(20, 2, 'Gỏi gà ta', 'goi_ga_ta1462253480.jpg', 'Cách làm gỏi gà rau răm đặc biệt theo phong cách miền Trung', '<p>Gỏi g&agrave; c&oacute; lẽ đ&atilde; trở th&agrave;nh một m&oacute;n ăn chẳng c&ograve;n xa lạ g&igrave; đối với hầu hết ch&uacute;ng ta, đặc biệt l&agrave; m&oacute;n gỏi g&agrave; rau răm &ndash; dường như hai loại thực phẩm n&agrave;y l&uacute;c n&agrave;o cũng được c&aacute;c chị em nội trợ cho &ldquo;kết đ&ocirc;i&rdquo;.<br />\r\nH&ocirc;m nay,&nbsp;<strong>nau.vn</strong>&nbsp;sẽ hướng dẫn c&aacute;c bạn&nbsp;<strong>c&aacute;ch l&agrave;m gỏi g&agrave; rau răm</strong>, cũng l&agrave; m&oacute;n ăn quen thuộc ấy nhưng theo phong c&aacute;ch của miền Trung. C&ugrave;ng xem thử&nbsp;<strong>c&aacute;ch l&agrave;m gỏi g&agrave; rau răm</strong>&nbsp;của miền Trung c&oacute; những đặc sắc g&igrave; nh&eacute;!</p>\r\n\r\n<p><a href="http://nau.vn/wp-content/uploads/2015/01/cach-lam-goi-ga-rau-ram.jpg"><img alt="cách làm gỏi gà rau răm" src="http://nau.vn/wp-content/uploads/2015/01/cach-lam-goi-ga-rau-ram-300x225.jpg" style="height:263px; width:351px" /></a></p>\r\n\r\n<p>c&aacute;ch l&agrave;m gỏi g&agrave; rau răm</p>\r\n\r\n<h2>&nbsp;</h2>\r\n\r\n<p>* Nguy&ecirc;n liệu l&agrave;m gỏi g&agrave; rau răm:</p>\r\n\r\n<p>+ 1 con g&agrave; ta<br />\r\n+ 100g bắp chuối hột<br />\r\n+ 1 củ h&agrave;nh t&acirc;y, 1 tr&aacute;i ớt sừng xanh<br />\r\n+ Rau răm<br />\r\n+ Muối, ti&ecirc;u, đường, bột ngọt, giấm gạo</p>\r\n\r\n<h2>* C&aacute;ch l&agrave;m gỏi g&agrave; rau răm:</h2>\r\n\r\n<p>&ndash; G&agrave; c&aacute;c bạn cần luộc trước khi tiến h&agrave;nh trộn gỏi. Nau.vn m&aacute;ch nhỏ c&aacute;c bạn c&aacute;ch luộc g&agrave; ngon: n&ecirc;n d&ugrave;ng nước lạnh để luộc g&agrave;, n&ecirc;m v&agrave;o nước luộc &iacute;t hạt n&ecirc;m v&agrave; 2 củ h&agrave;nh t&acirc;y đập dập, luộc trong khoảng 15 ph&uacute;t rồi vớt ra ng&acirc;m trong nước lạnh. C&aacute;c bạn lưu &yacute;, h&atilde;y để d&agrave;nh lại phần nước luộc g&agrave; để l&agrave;m nước trộn gỏi, sẽ gi&uacute;p m&oacute;n gỏi g&agrave; của c&aacute;c bạn c&oacute; vị b&eacute;o ngon hơn nh&eacute;.</p>\r\n\r\n<p>&ndash; Bắp chuối hột c&aacute;c bạn cắt sợi, h&agrave;nh t&acirc;y cắt l&aacute;t ng&acirc;m v&agrave;o nước lạnh để tăng độ gi&ograve;n. C&aacute;c bạn lưu &yacute; nh&eacute;, h&agrave;nh t&acirc;y h&atilde;y cắt sợi theo chiều ngang, khi ng&acirc;m v&agrave;o nước lạnh sẽ gi&uacute;p ra hết vị hăng của h&agrave;nh t&acirc;y đấy.</p>\r\n\r\n<p>&ndash; L&agrave;m nước trộn gỏi: cho ớt cắt l&aacute;t v&agrave; rau răm cắt nhuyễn v&agrave;o cối gi&atilde; nhỏ, sau đ&oacute; cho v&agrave;o ch&eacute;n, th&ecirc;m v&agrave;o 4 muỗng canh giấm gạo, 1 muỗng c&agrave; ph&ecirc; đường, nửa muỗng c&agrave; ph&ecirc; ti&ecirc;u, nửa muỗng c&agrave; ph&ecirc; bột ngọt, nửa muỗng c&agrave; ph&ecirc; muối, khoảng 2 muỗng canh nước luộc g&agrave; để tăng độ b&eacute;o. Trộn đều hỗn hợp.</p>\r\n\r\n<p>&ndash; Trộn gỏi: để gỏi g&agrave; ngon hơn, trước khi trộn, c&aacute;c bạn n&ecirc;n ướp g&agrave; với một &iacute;t ti&ecirc;u, nửa muỗng c&agrave; ph&ecirc; muối v&agrave; 1/4 muỗng c&agrave; ph&ecirc; bột ngọt. Sau đ&oacute; lần lượt cho bắp chuối, h&agrave;nh t&acirc;y v&agrave; rau răm c&ugrave;ng với nước trộn gỏi, trộn đều l&ecirc;n l&agrave; c&oacute; thể thưởng thức được rồi. Ăn k&egrave;m với nước mắm chua ngọt v&agrave; b&aacute;nh phồng t&ocirc;m bạn nh&eacute;.</p>\r\n\r\n<p>Cho d&ugrave; bạn d&ugrave;ng m&oacute;n n&agrave;y để ăn với cơm hay l&agrave;m khai vị, n&oacute; cũng sẽ ph&aacute;t huy hết sự tuyệt vời của m&igrave;nh tr&ecirc;n b&agrave;n ăn cho xem. Nếu bạn chưa từng thực hiện&nbsp;<strong><em>c&aacute;ch l&agrave;m gỏi g&agrave; rau răm</em></strong>&nbsp;của miền Trung th&igrave; h&atilde;y n&ecirc;n thử đi nh&eacute;, tin chắc bạn sẽ cảm thấy rất th&uacute; vị đấy!</p>\r\n', 35000, 1462253480, 1462253480, 0, NULL, 21),
(21, 1, 'Cơm chiên', 'com_chien1462433622.jpg', 'Ngày nay, khi đối diện với nền kinh tế có nhiều chuyển biến thiếu tích cực', '<p>Ng&agrave;y nay, khi đối diện với nền kinh tế c&oacute; nhiều chuyển biến thiếu t&iacute;ch cực, nguồn thu nhập c&oacute; phần hạn chế, gi&aacute; cả leo thang th&igrave; việc tiết kiệm tiền, tiết kiệm chi ph&iacute; sinh hoạt đ&oacute;ng vai tr&ograve; v&ocirc; c&ugrave;ng quan trọng. Đồng nghĩa với n&oacute; th&igrave; chị em nội trợ c&agrave;ng phải đảm đang hơn trong việc chế biến n&ecirc;n những m&oacute;n ăn vừa ngon, vừa bổ, vừa rẻ để c&oacute; thể chăm s&oacute;c sức khỏe cả gia đ&igrave;nh hiệu quả m&agrave; vẫn tiết kiệm được c&aacute;c khoản chi ti&ecirc;u. V&agrave;&nbsp;<strong>c&aacute;ch l&agrave;m cơm chi&ecirc;n trứng</strong>&nbsp;dưới đ&acirc;y cũng l&agrave; một sự lựa chọn kh&ocirc;n ngoan cho bữa s&aacute;ng h&agrave;ng ng&agrave;y của gia đ&igrave;nh bạn đấy.</p>\r\n\r\n<h2>C&aacute;ch l&agrave;m cơm chi&ecirc;n trứng</h2>\r\n\r\n<h3>Chuẩn bị nguy&ecirc;n liệu l&agrave;m cơm chi&ecirc;n trứng</h3>\r\n\r\n<p>&ndash; Cơm ch&iacute;n: 6 ch&eacute;n. Cứ 6 ch&eacute;n cơm ch&iacute;n sẽ l&agrave;m được 4 ch&eacute;n&nbsp;<strong>cơm chi&ecirc;n trứng</strong>&nbsp;nh&eacute;, bạn n&ecirc;n nấu cơm sẵn từ đ&ecirc;m h&ocirc;m trước với lượng nước &iacute;t hơn một ch&uacute;t để tiết kiệm thời gian chế biến, đồng thời nếu cơm kh&ocirc; th&igrave; m&oacute;n cơm chi&ecirc;n sẽ ngon hơn rất nhiều.</p>\r\n\r\n<p style="text-align: center;"><img alt="Chuẩn bị nguyên liệu làm cơm chiên trứng, chuan bi nguyen lieu lam com chien trung" src="http://monan9.com/wp-content/uploads/2015/04/Chuan-bi-nguyen-lieu-lam-com-chien-trung.jpg" style="height:308px; width:440px" /></p>\r\n\r\n<p>&ndash; Trứng g&agrave;: 2 quả, n&ecirc;n chọn trứng g&agrave; ta cho thơm ngon v&agrave; dinh dưỡng.</p>\r\n\r\n<p>&ndash; H&agrave;nh l&aacute;, rau m&ugrave;i: 50g.</p>\r\n\r\n<p>&ndash; Mỡ heo: 100g.</p>\r\n\r\n<p>&ndash; Tỏi: 3 củ.</p>\r\n\r\n<p>&ndash; Ớt sừng: 3 tr&aacute;i.</p>\r\n\r\n<p>&ndash; Gia vị: Hạt n&ecirc;m, bột ngọt, ớt bột, ti&ecirc;u, nước tương.</p>\r\n\r\n<h3>Sơ chế nguy&ecirc;n liệu l&agrave;m cơm chi&ecirc;n trứng</h3>\r\n\r\n<p>&ndash; Cơm: Xới tơi đều, để nguội hẳn.</p>\r\n\r\n<p>&ndash; Tỏi: L&agrave;m sạch, băm nhỏ.</p>\r\n\r\n<p>&ndash; Mỡ heo: rửa sạch, để r&aacute;o, th&aacute;i miếng nhỏ, luộc sơ, chi&ecirc;n ch&iacute;n v&agrave;ng nhẹ.</p>\r\n\r\n<p>&ndash; Ớt sừng: bỏ cuống, rửa sạch, th&aacute;i l&aacute;t trộn với 3-4 th&igrave;a nước tương để ăn k&egrave;m&nbsp;<strong>cơm chi&ecirc;n trứng</strong>.</p>\r\n\r\n<p>&ndash; H&agrave;nh l&aacute;, rau m&ugrave;i: Nhặt v&agrave; rửa sạch, để r&aacute;o, th&aacute;i nhỏ.</p>\r\n\r\n<p>&ndash; Trứng g&agrave;: Đ&aacute;nh tan với một &iacute;t muối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="Sơ chế nguyên liệu làm cơm chiên trứng, so che nguyen lieu lam com chien trung" src="http://monan9.com/wp-content/uploads/2015/04/So-che-nguyen-lieu-lam-com-chien-trung.jpg" style="height:312px; width:463px" /></p>\r\n\r\n<h3>Thực hiện l&agrave;m cơm chi&ecirc;n trứng</h3>\r\n\r\n<p>&ndash; Sử dụng to&agrave;n bộ phần t&oacute;p mỡ v&agrave; mỡ nước phi thơm với tỏi băm nhỏ v&agrave; một th&igrave;a ớt bột để&nbsp;<strong>c&aacute;ch l&agrave;m</strong>&nbsp;<strong>cơm chi&ecirc;n trứng</strong>&nbsp;c&oacute; m&agrave;u sắc hấp dẫn, nếu gia đ&igrave;nh bạn kh&ocirc;ng th&iacute;ch ăn cay hoặc c&oacute; trẻ nhỏ th&igrave; thay thế bằng &frac14; th&igrave;a bột điều đỏ nh&eacute;.</p>\r\n\r\n<p>&ndash; Cho to&agrave;n bộ cơm v&agrave;o, vặn lửa nhỏ vừa để chi&ecirc;n cơm, n&ecirc;m th&ecirc;m 2 th&igrave;a hạt n&ecirc;m, 1 th&igrave;a bột ngọt rồi tiếp tục đảo đều tay trong 15 &ndash; 20 ph&uacute;t để từng hạt cơm ngấm gia vị, gi&ograve;n ngon. Tiếp đ&oacute; bạn cho trứng đ&atilde; đ&aacute;nh tan v&agrave;o, đảo thật đều tay để trứng kh&ocirc;ng l&agrave;m cơm v&oacute;n cục v&agrave; bao phủ quanh từng hạt tr&ocirc;ng thơm ngon, hấp dẫn hơn. Đảo th&ecirc;m chừng 5 ph&uacute;t nữa, n&ecirc;m nếm lại gia vị sao cho vừa ăn. Cuối c&ugrave;ng cho h&agrave;nh l&aacute;, rau m&ugrave;i th&aacute;i nhỏ v&agrave; 1 th&igrave;a ti&ecirc;u bột v&agrave;o, đảo th&ecirc;m v&agrave;i lần nữa rồi tắt bếp l&agrave; bạn đ&atilde; ho&agrave;n th&agrave;nh xong&nbsp;<strong>c&aacute;ch l&agrave;m</strong>&nbsp;<strong>m&oacute;n cơm chi&ecirc;n trứng</strong>&nbsp;ngon bổ rẻ cho bữa s&aacute;ng của cả gia đ&igrave;nh rồi đấy.</p>\r\n\r\n<h3>Y&ecirc;u cầu v&agrave; b&iacute; quyết l&agrave;m m&oacute;n cơm chi&ecirc;n trứng ngon</h3>\r\n\r\n<p>&ndash; M&oacute;n&nbsp;<strong>cơm chi&ecirc;n trứng</strong>&nbsp;c&oacute; m&ugrave;i thơm hấp dẫn, vị vừa ăn, từng hạt cơm tơi, đều, được bao phủ bởi một lớp trứng rất đẹp mắt, d&ugrave;ng kem nước tương trộn ớt th&aacute;i l&aacute;t rất hợp vị.</p>\r\n\r\n<p>&ndash; B&iacute; quyết để l&agrave;m m&oacute;n&nbsp;<strong>cơm chi&ecirc;n trứng</strong>&nbsp;ngon: Cơm phải tơi, kh&ocirc; đều, bạn n&ecirc;n d&ugrave;ng gạo thường để nấu, kh&ocirc;ng n&ecirc;n d&ugrave;ng gạo dẻo nh&eacute;. Đặc biệt, bạn c&oacute; thể sử dụng dầu ăn để chi&ecirc;n nhưng sử dụng mỡ nước v&agrave; t&oacute;p mỡ th&igrave; m&oacute;n&nbsp;<strong>cơm chi&ecirc;n trứng</strong>&nbsp;sẽ b&eacute;o ngậy, ngon hơn rất nhiều.</p>\r\n\r\n<p>Thật đơn giản v&agrave; tiết kiệm nhưng vẫn bổ dưỡng phải kh&ocirc;ng n&agrave;o. Với&nbsp;<strong>c&aacute;ch l&agrave;m cơm chi&ecirc;n trứng</strong>&nbsp;sẽ gi&uacute;p cho bữa s&aacute;ng của cả gia đ&igrave;nh bạn ngon miệng hơn rất nhiều v&agrave; đặc biệt gi&uacute;p bạn tiết kiệm được một khoản chi ph&iacute; kh&ocirc;ng nhỏ đ&acirc;u đấy.</p>\r\n\r\n<p>Ch&uacute;c bạn chế biến m&oacute;n ăn th&agrave;nh c&ocirc;ng v&agrave; lu&ocirc;n l&agrave; người nội trợ đảm đang, kh&eacute;o l&eacute;o của cả gia đ&igrave;nh nh&eacute;!</p>\r\n', 77000, 1462433622, 1462433622, 0, NULL, 21),
(22, 1, 'Phở bò', 'pho_bo1462433696.jpg', 'Cách nấu phở bò Hà Nội, Nam Định ngon theo công thức gia truyền ', '<p>Sau đ&acirc;y, vaobepnauan.com xin giới thiệu với c&aacute;c bạn c&aacute;c c&ocirc;ng thức gia truyền của nấu m&oacute;n phở b&ograve; ngon của người H&agrave; Nội, Nam Định n&oacute;i ri&ecirc;ng v&agrave; miền Bắc n&oacute;i chung.</p>\r\n\r\n<h3>C&ocirc;ng thức gia truyền nấu m&oacute;n phở b&ograve; H&agrave; Nội</h3>\r\n\r\n<p><strong>Nguy&ecirc;n liệu (3-4 b&aacute;t phở)</strong></p>\r\n\r\n<p>0.5kg đu&ocirc;i b&ograve;<br />\r\n0.5kg sườn b&ograve;<br />\r\n0.5kg bắp b&ograve; (hoặc thay bằng thịt nạm, gầu t&ugrave;y th&iacute;ch)<br />\r\nthịt b&ograve; t&aacute;i (t&ugrave;y th&iacute;ch)<br />\r\n&frac14; củ h&agrave;nh t&acirc;y to<br />\r\n1 củ gừng (to khoảng gấp rưỡi ng&oacute;n tay c&aacute;i)<br />\r\n5-6 củ h&agrave;nh kh&ocirc; (h&agrave;nh hương, c&oacute; thể thay bằng h&agrave;nh t&iacute;m)<br />\r\n1 th&igrave;a caf&eacute; hạt m&ugrave;i gi&agrave; (kh&ocirc;ng bắt buộc)<br />\r\n5-6 rễ c&acirc;y m&ugrave;i<br />\r\n1 thảo quả<br />\r\n2 hoa hồi<br />\r\n1 thanh quế nhỏ<br />\r\n2 l&oacute;ng m&iacute;a (mỗi l&oacute;ng d&agrave;i khoảng 10cm)<br />\r\nBột n&ecirc;m hoặc muối<br />\r\nB&aacute;nh phở<br />\r\nH&agrave;nh, m&ugrave;i th&aacute;i nhỏ<br />\r\nTương ớt, chanh</p>\r\n\r\n<p><strong>C&aacute;ch l&agrave;m</strong></p>\r\n\r\n<p>1. Đu&ocirc;i v&agrave; sườn b&ograve; chặt miếng nhỏ. Thịt bắp b&ograve; để nguy&ecirc;n miếng. Pha nước muối lo&atilde;ng (mặn vừa như nấu canh l&agrave; được), ng&acirc;m đu&ocirc;i b&ograve;, sườn b&ograve; v&agrave; thịt b&ograve; trong khoảng 2h. Thịt b&ograve; giờ kh&ocirc;ng được sạch lắm, nhất l&agrave; phần đu&ocirc;i c&oacute; thể c&ograve;n c&oacute; m&ugrave;i h&ocirc;i, việc ng&acirc;m muối sẽ gi&uacute;p cho thịt &ldquo;sạch&rdquo; hơn, khi ăn cũng mềm ngon hơn.</p>\r\n\r\n<p>2. Trong l&uacute;c đợi ng&acirc;m thịt th&igrave; chuẩn bị c&aacute;c nguy&ecirc;n liệu kh&aacute;c:</p>\r\n\r\n<p>&ndash; H&agrave;nh t&acirc;y, h&agrave;nh kh&ocirc; (h&agrave;nh hương), gừng, m&iacute;a để nguy&ecirc;n vỏ, nướng ch&iacute;n thơm. M&igrave;nh nướng trực tiếp tr&ecirc;n bếp điện, để lửa vừa để c&aacute;c thứ h&agrave;nh gừng c&oacute; thể ch&iacute;n b&ecirc;n trong m&agrave; b&ecirc;n vỏ ngo&agrave;i kh&ocirc;ng bị ch&aacute;y qu&aacute; mức. Sau khi nướng xong th&igrave; cạo sạch vỏ gừng v&agrave; h&agrave;nh (m&iacute;a để nguy&ecirc;n vỏ). Rửa lại tất cả cho sạch. H&agrave;nh t&acirc;y c&oacute; thể bổ đ&ocirc;i hoặc bổ tư. Gừng đập dập hoặc th&aacute;i l&aacute;t.</p>\r\n\r\n<p>&ndash; Rễ m&ugrave;i rửa sạch.</p>\r\n\r\n<p>&ndash; Hoa hồi, quế, thảo quả, hạt m&ugrave;i (nếu c&oacute;) cho l&ecirc;n chảo rang ở lửa vừa đến khi dậy m&ugrave;i thơm. *Lưu &yacute;: kh&ocirc;ng rang ở lửa qu&aacute; to hoặc qu&aacute; kĩ v&igrave; c&aacute;c loại gia vị c&oacute; thể bị ch&aacute;y. Cho tất cả v&agrave;o t&uacute;i vải, buộc chặt miệng.</p>\r\n\r\n<p style="text-align: center;"><img alt="cách nấu phở bò gia truyền 1" src="http://vaobepnauan.com/wp-content/uploads/2014/08/cach-nau-pho-bo-gia-truyen-1.jpg" style="height:400px; width:500px" /></p>\r\n\r\n<p>3. Đổ hết nước ng&acirc;m, rửa lại đu&ocirc;i b&ograve;, sườn b&ograve; v&agrave; thịt b&ograve; bắp. Cho tất cả v&agrave;o nồi, đổ nước lạnh ngập thịt rồi vặn lửa to đun s&ocirc;i. Đợi nước s&ocirc;i th&igrave; hạ lửa nhỏ đun th&ecirc;m khoảng 1-2 ph&uacute;t rồi bỏ nước luộc n&agrave;y đi, rửa lại cho thật sạch vụn thịt v&agrave; bọt bẩn. Nếu thịt bẩn th&igrave; c&oacute; thể lặp lại bước luộc rửa n&agrave;y 1 lần nữa. C&aacute;c bạn đừng lo sẽ l&agrave;m mất chất ngọt của thịt v&igrave; thật ra chất ngọt sẽ tiết ra từ xương trong qu&aacute; tr&igrave;nh hầm, việc bỏ nước luộc n&agrave;y đi chỉ gi&uacute;p nước d&ugrave;ng trong v&agrave; ngon hơn th&ocirc;i, nếu c&oacute; mất &iacute;t chất ngọt cũng kh&ocirc;ng đ&aacute;ng kể. Luộc với nước s&ocirc;i th&ecirc;m 1-2 lần cho thật sạch.</p>\r\n\r\n<p>4. Cho đu&ocirc;i b&ograve;, sườn b&ograve; v&agrave; thịt v&agrave;o nồi, đổ th&ecirc;m 4-5 b&aacute;t to nước lạnh. Vặn lửa to, đợi nước s&ocirc;i th&igrave; hớt bọt (nếu c&oacute;). Cho c&aacute;c thứ gia vị đ&atilde; chuẩn bị v&agrave;o nồi, gồm: H&agrave;nh t&acirc;y, h&agrave;nh hương, gừng, m&iacute;a, rễ m&ugrave;i, t&uacute;i đựng hoa hồi, thảo quả, quế, hạt m&ugrave;i. N&ecirc;m bột canh, bột gia vị hoặc muối. Kh&ocirc;ng n&ecirc;n d&ugrave;ng nước mắm v&igrave; c&oacute; thể l&agrave;m nước phở bị chua.</p>\r\n\r\n<p>Để lửa nhỏ ninh trong khoảng 1.5-2h nếu d&ugrave;ng nồi thường, nồi &aacute;p suất sẽ nhanh hơn. Lưu &yacute; l&agrave; thịt b&ograve; bắp sẽ cần vớt ra trước, tr&aacute;nh để thịt bị qu&aacute; mềm v&agrave; n&aacute;t. B&ograve; bắp sau khi vớt ra c&oacute; thể ng&acirc;m trong b&aacute;t nước lọc (nước đun s&ocirc;i để nguội), rồi th&aacute;i l&aacute;t mỏng.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh đun c&oacute; thể cần hớt bọt cho nước trong. Nếu cảm thấy thiếu nước th&igrave; th&ecirc;m nước s&ocirc;i (kh&ocirc;ng d&ugrave;ng nước lạnh).</p>\r\n\r\n<p>Sau khi đu&ocirc;i v&agrave; sườn b&ograve; đ&atilde; mềm nhừ th&igrave; n&ecirc;m lại gia vị/ bột canh hoặc muối cho vừa ăn. C&oacute; thể n&ecirc;m hơi nhạt một ch&uacute;t để khi ăn cho th&ecirc;m nước mắm. Nước d&ugrave;ng đạt sẽ c&oacute; m&agrave;u v&agrave;ng nhẹ, trong v&agrave; hơi s&aacute;nh mỡ, c&oacute; vị ngọt tự nhi&ecirc;n từ xương v&agrave; m&iacute;a, hương vị đậm đ&agrave;.</p>\r\n\r\n<p>5. Chuẩn bị b&aacute;nh phở v&agrave; c&aacute;c loại rau thơm ăn k&egrave;m (H&agrave;nh, m&ugrave;i rửa sạch, th&aacute;i h&agrave;nh xanh th&agrave;nh khoanh tr&ograve;n nhỏ, chẻ phần củ h&agrave;nh trắng, m&ugrave;i th&aacute;i nhỏ&hellip;).</p>\r\n\r\n<p>6. Trước khi ăn n&ecirc;n trần lại b&aacute;t ăn phở v&agrave; b&aacute;nh phở qua nước s&ocirc;i. Hai thứ n&agrave;y n&oacute;ng sẽ gi&uacute;p b&aacute;t phở ngon hơn. Sắp b&aacute;nh phở v&agrave;o b&aacute;t, sắp thịt, c&aacute;c loại rau thơm l&ecirc;n tr&ecirc;n. Đun nước d&ugrave;ng cho thật s&ocirc;i rồi chan v&agrave;o b&aacute;t. D&ugrave;ng n&oacute;ng với chanh tươi v&agrave; (tương) ớt.</p>\r\n\r\n<p style="text-align: center;"><img alt="cách nấu phở bò gia truyền 2" src="http://vaobepnauan.com/wp-content/uploads/2014/08/cach-nau-pho-bo-gia-truyen-2.jpg" style="height:284px; width:499px" /></p>\r\n\r\n<h3>C&ocirc;ng thức truyền thống nấu phở b&ograve; kiểu Bắc</h3>\r\n\r\n<p>Nếu hỏi 100 người nước ngo&agrave;i về đồ ăn Việt th&igrave; m&igrave;nh tin rằng 99 người sẽ nhắc tới Phở đầu ti&ecirc;n v&igrave; thế cũng thật l&agrave; gi&aacute; trị nếu tự m&igrave;nh c&oacute; thể nấu được một b&aacute;t phở ngon phải kh&ocirc;ng c&aacute;c bạn. Tin m&igrave;nh đi, đọc hết b&agrave;i n&agrave;y mọi người sẽ thấy ai cũng c&oacute; thể nấu phở, chỉ cần một ch&uacute;t ki&ecirc;n tr&igrave; v&agrave; t&igrave;nh y&ecirc;u d&agrave;nh cho phở th&ocirc;i nh&eacute;.</p>\r\n\r\n<p><strong>1. Nguy&ecirc;n liệu: cho khoảng 7 &ndash; 8 b&aacute;t phở</strong></p>\r\n\r\n<p>Xương b&ograve;: 3 kh&uacute;c</p>\r\n\r\n<p>Thịt b&ograve;: khoảng 400 &ndash; 500 grams (t&ugrave;y thuộc v&agrave;o sở th&iacute;ch ăn nhiều thịt hay &iacute;t thịt nữa)</p>\r\n\r\n<p>Nửa g&oacute;i gia vị nấu phở loại 85 grams. G&oacute;i gia vị phở khi mua sẵn, th&ocirc;ng thường sẽ gồm: hồi (hay c&ograve;n gọi l&agrave; đại hồi &ndash; star anise), hột m&ugrave;i hay hột ng&ograve; (coriander), quế (cinnamon), đinh hương (clove) v&agrave; thảo quả (black cardamom). Nếu chỗ ai kh&ocirc;ng mua được g&oacute;i gia vị nấu phở sẵn th&igrave; c&oacute; thể mua ri&ecirc;ng 5 loại gia vị n&agrave;y v&agrave; pha chế theo c&aacute;ch của m&igrave;nh để nấu phở.</p>\r\n\r\n<p style="text-align: center;"><img alt="cách nấu phở bò gia truyền 3" src="http://vaobepnauan.com/wp-content/uploads/2014/08/cach-nau-pho-bo-gia-truyen-3.jpg" style="height:315px; width:529px" /></p>\r\n\r\n<p>Gia vị nấu phở truyền thống</p>\r\n\r\n<p>H&agrave;nh kh&ocirc;: 4 &ndash; 5 củ nhỏ</p>\r\n\r\n<p>H&agrave;nh t&acirc;y: 1 củ (ai c&oacute; nhiều h&agrave;nh kh&ocirc; c&oacute; thể bỏ lượng h&agrave;nh t&acirc;y v&agrave; l&agrave;m lượng h&agrave;nh kh&ocirc; gấp đ&ocirc;i l&ecirc;n nh&eacute;)</p>\r\n\r\n<p>1 củ gừng to</p>\r\n\r\n<p>1 g&oacute;i phở kh&ocirc;</p>\r\n\r\n<p>H&agrave;nh, m&ugrave;i tươi</p>\r\n', 26000, 1462433696, 1462433696, 0, NULL, 26);
INSERT INTO `food` (`id_food`, `id_type`, `name_food`, `image_food`, `mini_content`, `content`, `unit_price`, `time_add`, `time_edit`, `today`, `author`, `cook`) VALUES
(23, 5, 'Gỏi cuốn', 'goi_cuon1462433783.jpg', 'Năm 2011, gỏi cuốn có mặt trong  "50 món ăn ngon nhất thế giới"', '<p><strong>Nguy&ecirc;n liệu:</strong></p>\r\n\r\n<p>- 500g T&ocirc;m</p>\r\n\r\n<p>-700g thịt ba rọi hoặc đ&ugrave;i (t&ugrave;y th&iacute;ch mỡ nhiều hay nạc nhiều)</p>\r\n\r\n<p>- 1 xấp b&aacute;nh tr&aacute;ng</p>\r\n\r\n<p>- 500g b&uacute;n tươi</p>\r\n\r\n<p>- Tương hột xay</p>\r\n\r\n<p>- X&agrave;-l&aacute;ch, rau sống, hẹ</p>\r\n\r\n<p>- Đồ chua, đậu phộng rang gi&atilde; nhuyễn (t&ugrave;y th&iacute;ch)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 1" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-nguyen-lieu.jpg" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;ch l&agrave;m:</strong></p>\r\n\r\n<p><strong>Bước 1:</strong>&nbsp;Rau sống rửa sạch, để r&aacute;o</p>\r\n\r\n<p><strong>Bước 2:</strong>&nbsp;Chuẩn bị t&ocirc;m:</p>\r\n\r\n<p>T&ocirc;m rửa sạch, cho v&agrave;o 1/2 muỗng cafe muối, 1 muỗng cafe đường, 1-2 muỗng canh rượu (kh&ocirc;ng cần cho nước, t&ocirc;m sẽ tự ra nước). Việc th&ecirc;m gia vị gi&uacute;p t&ocirc;m đậm đ&agrave; v&agrave; kh&ocirc;ng tanh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 2" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-01.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đậy nắp, mở lửa nấu cho t&ocirc;m chuyển đỏ, đảo đều. Vớt t&ocirc;m ra rổ để r&aacute;o. Lột vỏ, xẻ đ&ocirc;i t&ocirc;m, bỏ chỉ lưng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 3" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-02.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 3:&nbsp;</strong>Chuẩn bị thịt:</p>\r\n\r\n<p>Thịt cạo l&ocirc;ng, rửa sạch, cho đun s&ocirc;i nồi nước, thả v&agrave;o 1 củ h&agrave;nh đập dập, cho thịt v&agrave;o luộc chừng 20 ph&uacute;t l&agrave; thịt ch&iacute;n, vớt thịt ra thau nước lạnh gi&uacute;p thịt trắng v&agrave; gi&ograve;n.Th&aacute;i thịt th&agrave;nh lắt mỏng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 4" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-03.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 5" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-04.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 6" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-05.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 7" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-06.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 4:&nbsp;</strong>Chuẩn bị cuốn:</p>\r\n\r\n<p>Sắp t&ocirc;m, thịt, b&uacute;n, rau, b&aacute;nh tr&aacute;ng gần tầm tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 8" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-07.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m ướt b&aacute;nh tr&aacute;ng, l&oacute;t rau b&ecirc;n dưới, xếp b&uacute;n, thịt, t&ocirc;m, đặt cọng hẹ trang tr&iacute; (vừa thơm, vừa đẹp). Cuộn chắc tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 9" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-08.jpg" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dọn&nbsp;<strong>gỏi cuốn t&ocirc;m thịt</strong>&nbsp;với tương, rắc th&ecirc;m đậu phộng v&agrave; đồ chua nếu th&iacute;ch.</p>\r\n\r\n<p><strong>Bước 5:&nbsp;</strong>L&agrave;m tương: Phi thơm &iacute;t tỏi với dầu, cho tương hột xay v&agrave;o x&agrave;o, n&ecirc;m đường cho bớt mặn.</p>\r\n\r\n<p><em>Gỏi cuốn t&ocirc;m thịt</em>&nbsp;c&oacute; đầy đủ chất dinh dưỡng: chất đường bột từ b&uacute;n, chất đạm từ t&ocirc;m thịt, chất b&eacute;o trong thịt mỡ, chất xơ trong rau.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 10" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-9.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 11" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-10.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://eva-img-cdn2.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="cuoi tuan lam goi cuon tom thit an choi - 12" src="http://anh.eva.vn/upload/4-2013/images/2013-10-26/1382752961-11.JPG" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&oacute;n&nbsp;<u>gỏi cuốn t&ocirc;m thịt</u>&nbsp;n&agrave;y d&ugrave;ng khai vị hoặc ăn chơi, hoặc l&agrave;m m&oacute;n đổi bữa cũng tiện v&ocirc; c&ugrave;ng. Nhờ t&iacute;nh chất nhiều rau n&ecirc;n kh&ocirc;ng sợ tăng c&acirc;n bạn nh&eacute;!</p>\r\n', 55000, 1462433783, 1462433783, 0, NULL, 1);

