INSERT INTO `post` (`id_post`, `title`, `mini_content`, `content`, `image`, `author`, `time_post`, `time_edit`, `n_views`, `slide_show`) VALUES
(6, '5 nguyên tắc để tự thiết kế bữa ăn “khỏe đẹp” ngay tại nhà', 'Thay vì đi theo một chế độ ăn kiêng dựng sẵn, bạn hoàn toàn có thể ăn uống theo sở thích mà vẫn giảm cân nhờ các nguyên tắc sau.', '<p>Ch&uacute;ng ta c&oacute; thể dễ d&agrave;ng t&igrave;m thấy v&ocirc; v&agrave;n những c&ocirc;ng thức ăn uống khoa học tr&ecirc;n internet hay ở s&aacute;ch b&aacute;o, tạp ch&iacute;. Tuy nhi&ecirc;n về l&acirc;u d&agrave;i, để theo đuổi một khẩu phần&nbsp;<a href="http://kenh14.vn/dep-/phan-tich-loi-hai-trong-thuc-don-an-kieng-than-toc-cua-sao-han-20150815102450148.chn" target="_blank" title="ăn">ăn</a>&nbsp;n&agrave;o đ&oacute; kh&ocirc;ng phải l&agrave; chuyện dễ d&agrave;ng. Đ&oacute; ch&iacute;nh l&agrave; l&iacute; do v&igrave; sao mỗi người n&ecirc;n tự thiết kế cho m&igrave;nh một th&oacute;i quen ăn uống vừa l&agrave;nh mạnh lại ph&ugrave; hợp với t&igrave;nh h&igrave;nh cơ thể của bản th&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đặt cho m&igrave;nh mục ti&ecirc;u hợp l&yacute;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&uacute;ng ta kh&ocirc;ng n&ecirc;n đặt ra một mục ti&ecirc;u xa vời, vượt ngo&agrave;i khả năng của m&igrave;nh. V&iacute; dụ như mục ti&ecirc;u cắt giảm ho&agrave;n to&agrave;n tinh bột v&agrave; đường, chỉ ăn rau, một tuần giảm 5kg&hellip; kh&ocirc;ng chỉ rất kh&oacute; thực hiện m&agrave; c&ograve;n tạo ra một &aacute;p lực đ&egrave; nặng t&acirc;m l&yacute;. Bước đầu thất bại sẽ khiến bạn trở n&ecirc;n thất vọng, ch&aacute;n nản v&agrave; rất dễ từ bỏ kế hoạch đề ra. Do đ&oacute; h&atilde;y đặt ra cho m&igrave;nh một chiến lược d&agrave;i hơi v&agrave; chia nhỏ để thực hiện theo từng giai đoạn. Những th&agrave;nh c&ocirc;ng ngắn hạn sẽ l&agrave; động lực để mỗi người theo đuổi chế độ ăn uống khoa học l&agrave;nh mạnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="a-17a94" src="http://k14.vcmedia.vn/thumb_w/600/5e11d41927/2015/10/23/a-17a94.png" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hiểu r&otilde; bản th&acirc;n</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng phải ngẫu nhi&ecirc;n khi hầu hết c&aacute;c chuy&ecirc;n gia dinh dưỡng đều khuy&ecirc;n rằng lắng nghe tiếng n&oacute;i của cơ thể l&agrave; yếu tố đầu ti&ecirc;n gi&uacute;p ch&uacute;ng ta duy tr&igrave; sức khỏe. Bạn kh&ocirc;ng thể tu&acirc;n theo một chế độ ăn ki&ecirc;ng với nhiều thực phẩm chua hay detox với nước chanh khi bản th&acirc;n bị đau dạ d&agrave;y. Bạn cũng kh&ocirc;ng thể theo đuổi một chế độ ăn to&agrave;n thịt b&ograve; với ước muốn tăng cơ trong khi hệ ti&ecirc;u h&oacute;a k&eacute;m&hellip; Biết cơ thể m&igrave;nh cần g&igrave; sẽ gi&uacute;p ch&uacute;ng ta đ&aacute;p ứng được hiệu quả hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thiết kế cho m&igrave;nh một chế độ ăn l&agrave;nh mạnh, đa dạng</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một chế độ ăn khoa học sẽ cung cấp cho cơ thể đầy đủ dưỡng chất v&agrave; vitamin cần thiết. Việc ch&uacute;ng ta chỉ ăn rau hoặc chỉ ăn thịt kh&ocirc;ng những phản khoa học, kh&ocirc;ng đem lại nhiều &iacute;ch lợi m&agrave; c&ograve;n khiến bản th&acirc;n ch&aacute;n ng&aacute;n mỗi khi tới bữa ăn. X&eacute;t về l&acirc;u d&agrave;i, chế độ ăn rất &iacute;t calo ho&agrave;n to&agrave;n kh&ocirc;ng tốt cho cơ thể.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nghi&ecirc;n cứu chỉ ra rằng ch&uacute;ng ta kh&ocirc;ng n&ecirc;n ăn dưới 1.200 calo/ng&agrave;y cho nam v&agrave; dưới 1.400 calo/ng&agrave;y cho nữ v&igrave; nếu bạn ăn qu&aacute; &iacute;t th&igrave; sẽ c&oacute; nguy cơ thiếu c&aacute;c chất dinh dưỡng cần thiết. Đa dạng h&oacute;a bữa ăn sẽ gi&uacute;p ch&uacute;ng ta ngon miệng hơn đồng thời đạt được mục ti&ecirc;u m&agrave; kh&ocirc;ng phải từ bỏ nhiều sở th&iacute;ch ăn uống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="1-0b4db" src="http://k14.vcmedia.vn/thumb_w/600/5e11d41927/2015/09/24/1-0b4db.jpg" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&oacute; sự hỗ trợ về tinh thần</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bản th&acirc;n mỗi người l&agrave; yếu tố quan trọng nhất, tuy nhi&ecirc;n, ho&agrave;n to&agrave;n kh&ocirc;ng thể phủ nhận vai tr&ograve; của gia đ&igrave;nh hay bạn b&egrave; trong qu&aacute; tr&igrave;nh h&igrave;nh th&agrave;nh n&ecirc;n th&oacute;i quen ăn uống, sinh hoạt. Những lời động vi&ecirc;n, hỗ trợ, nhắc nhở ch&iacute;nh l&agrave; một c&aacute;ch gi&uacute;p bạn mạnh mẽ hơn, quyết t&acirc;m hơn trong việc duy tr&igrave; khẩu phần ăn uống l&agrave;nh mạnh. Nếu c&oacute; thể th&igrave; h&atilde;y t&igrave;m kiếm một số người đồng h&agrave;nh c&ugrave;ng bạn thực hiện, điều n&agrave;y sẽ gi&uacute;p bạn c&oacute; nhiều sức mạnh v&agrave; niềm tin hơn đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thay đổi lối sống</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lối sống t&iacute;ch cực sẽ c&oacute; t&aacute;c động kh&ocirc;ng nhỏ đến c&aacute;ch bạn ăn uống mỗi ng&agrave;y. Đi ngủ l&uacute;c 10 &ndash; 11h sẽ gi&uacute;p ch&uacute;ng ta kh&ocirc;ng cảm thấy đ&oacute;i, từ đ&oacute; tr&aacute;nh được tật ăn khuya. Uống một cốc nước lọc khi ngủ dậy kh&ocirc;ng chỉ c&oacute; t&aacute;c dụng cung cấp nước cho cơ thể m&agrave; c&ograve;n khiến cho hệ ti&ecirc;u h&oacute;a hấp thụ bữa s&aacute;ng được thuận lợi v&agrave; dễ d&agrave;ng hơn. Hay việc tập thể dục thường xuy&ecirc;n, đều đặn ch&iacute;nh l&agrave; biện ph&aacute;p hữu hiệu nhất để đốt ch&aacute;y mỡ thừa, gi&uacute;p ch&uacute;ng ta giải ph&oacute;ng năng lượng để c&oacute; một bữa ăn ngon miệng m&agrave; vẫn giữ được v&oacute;c d&aacute;ng c&acirc;n đối, cơ thể khỏe mạnh.</p>\r\n', '1-0b4db1462253638.jpg', NULL, '0000-00-00', '0000-00-00', 13, 1),
(7, 'Tác dụng bất ngờ từ vỏ chuối không phải ai cũng biết', 'Có lẽ rất nhiều người không hề nghĩ rằng vỏ chuối lại có tác dụng tốt đến vậy.', '<p><strong>1. Gi&aacute; trị về dinh dưỡng</strong></p>\r\n\r\n<p>Chuối l&agrave; một trong những loại tr&aacute;i c&acirc;y được ti&ecirc;u thụ nhiều nhất tr&ecirc;n thế giới. Vỏ chuối cũng c&oacute; những gi&aacute; trị dinh dưỡng kh&ocirc;ng k&eacute;m, trong vỏ chuối chứa nhiều sắt, kali v&agrave; c&aacute;c vitamin B, C v&agrave; K, ngo&agrave;i ra chứa lượng lớn chất xơ, chất chống oxy h&oacute;a v&agrave; đồng. N&ecirc;n chọn những tr&aacute;i chuối &#39;sinh học&#39; nhằm tr&aacute;nh c&aacute;c h&oacute;a chất c&oacute; hại cho cơ thể.</p>\r\n\r\n<p><strong>2. Cải thiện đường ruột</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi bị t&aacute;o b&oacute;n hay c&aacute;c vấn đề ở ruột, vỏ chuối c&oacute; nhiều chất xơ gi&uacute;p giảm c&aacute;c rối loạn n&agrave;y.</p>\r\n\r\n<p><strong>3. Gi&uacute;p bảo vệ mắt</strong></p>\r\n\r\n<p>Vỏ chuối gi&agrave;u b&ecirc;ta caroten, gi&agrave;u vitamine A v&igrave; vậy rất cần cho mắt.</p>\r\n\r\n<p><strong>4. Gi&uacute;p giảm cholesterol</strong></p>\r\n\r\n<p>Nếu kết quả x&eacute;t nghiệm cho thấy bạn c&oacute; lượng cholesterol cao, h&atilde;y n&ecirc;n ăn chuối cả vỏ. Vỏ chuối c&oacute; đặc t&iacute;nh l&agrave;m sạch những chất b&eacute;o trong c&aacute;c động mạch.</p>\r\n\r\n<p><strong>5. Gi&uacute;p giảm đau đầu</strong></p>\r\n\r\n<p>&Aacute;p mặt trong của vỏ chuối l&ecirc;n tr&aacute;n, h&atilde;y để trong v&ograve;ng v&agrave;i ph&uacute;t. C&oacute; thể cố định bằng băng d&aacute;n để vỏ chuối khỏi di chuyển.</p>\r\n\r\n<table cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><img alt="Tác dụng bất ngờ từ vỏ chuối không phải ai cũng biết - Ảnh 1" src="http://media.doisongphapluat.com/thumb_x480x/448/2016/4/19/vochuoi28-1460996210.jpg" title="Tác dụng bất ngờ từ vỏ chuối không phải ai cũng biết - Ảnh 1" /></p>\r\n\r\n<p><strong>6. Gi&uacute;p chăm s&oacute;c da</strong></p>\r\n\r\n<p>Khi va chạm đ&acirc;u đ&oacute;, bị khối m&aacute;u tụ, bạn c&oacute; thể đắp mặt trong của vỏ chuối l&ecirc;n v&ugrave;ng da đ&oacute;, cố định bằng băng d&aacute;n v&agrave; c&oacute; thể suốt cả ng&agrave;y. Mặt kh&aacute;c nếu bị vảy nến c&oacute; thể d&ugrave;ng vỏ chuối để l&agrave;m dịu v&ugrave;ng da bị kh&ocirc; v&agrave; giảm cảm gi&aacute;c ngứa. C&oacute; thể ch&agrave; x&aacute;t mặt trong vỏ chuối l&ecirc;n phần da bị tổn thương hai lần mỗi ng&agrave;y v&agrave; cũng hiệu quả trong điều trị mụn trứng c&aacute;.</p>\r\n\r\n<p><strong>7. Gi&uacute;p giảm c&aacute;c vết nhăn ở mặt</strong></p>\r\n\r\n<p>Vỏ chuối l&agrave; một trong những phương thuốc hiệu quả gi&uacute;p giảm c&aacute;c vết nhăn. Mỗi buổi tối trước khi đi ngủ, ch&agrave; nhẹ phần b&ecirc;n trong của vỏ chuối l&ecirc;n da mặt, để vậy m&agrave; kh&ocirc;ng cần rửa. L&agrave;n da sẽ trở n&ecirc;n mềm mại hơn.</p>\r\n\r\n<p><strong>8. Gi&uacute;p cải thiện t&acirc;m trạng</strong></p>\r\n\r\n<p>Vỏ chuối chứa một lượng lớn tryptophan, axit amin n&agrave;y k&iacute;ch th&iacute;ch sản xuất serotonin được gọi l&agrave; &quot;hormone hạnh ph&uacute;c&quot;.Với hai vỏ chuối mỗi ng&agrave;y trong ba ng&agrave;y li&ecirc;n tiếp, bạn sẽ tăng 16% lượng serotonin trong cơ thể.</p>\r\n\r\n<p><strong>9. L&agrave;m trắng răng</strong></p>\r\n\r\n<p>Ch&agrave; phần b&ecirc;n trong của vỏ chuối l&ecirc;n răng v&agrave;i lần tr&ecirc;n tuần, răng của bạn sẽ s&aacute;ng l&ecirc;n tr&ocirc;ng thấy.</p>\r\n\r\n<p><strong>10. Mặt nạ dưỡng da</strong></p>\r\n\r\n<p>C&aacute;c chất dinh dưỡng trong vỏ chuối gi&uacute;p l&agrave;m sạch da. Rửa mặt bằng sữa rửa mặt v&agrave; nước ở pH trung t&iacute;nh. Đắp vỏ chuối tr&ecirc;n to&agrave;n bộ khu&ocirc;n mặt, để kh&ocirc;, sau đ&oacute; rửa sạch với nước ấm.</p>\r\n\r\n<p><strong>11. Giảm đau khi bị c&ocirc;n tr&ugrave;ng đốt</strong></p>\r\n\r\n<p>Nếu bạn bị muỗi hay kiến đốt, đắp vỏ chuối l&ecirc;n vết cắn trong một v&agrave;i ph&uacute;t, vết đau sẽ nhanh ch&oacute;ng dịu lại v&agrave; hết. Bạn cũng c&oacute; thể &aacute;p dụng c&aacute;ch n&agrave;y khi bị m&egrave;o c&agrave;o.</p>\r\n\r\n<p><strong>12. Loại bỏ mụn c&oacute;c</strong></p>\r\n\r\n<p>Đặt vỏ chuối l&ecirc;n mụn c&oacute;c sau đ&oacute; d&ugrave;ng băng quấn lại. Thay thường xuy&ecirc;n trong v&agrave;i ng&agrave;y. Sau một tuần c&aacute;c mụn c&oacute;c sẽ rơi hết.</p>\r\n\r\n<p><strong>13. Trị mụn trứng c&aacute;</strong></p>\r\n\r\n<p>Mỗi buổi tối, thoa một vỏ chuối l&ecirc;n mặt, bạn cứ để như vậy đi ngủ. S&aacute;ng ra rửa lại mặt như b&igrave;nh thường. Bạn c&oacute; thể &aacute;p dụng c&aacute;ch n&agrave;y cả tuần.</p>\r\n\r\n<p><strong>14. Điều trị vết bầm t&iacute;m</strong></p>\r\n\r\n<p>Đơn giản chỉ cần đặt một miếng v&agrave;o nơi bạn bị bầm, v&agrave; để chất dinh dưỡng k&igrave; diệu trong vỏ chuối gi&uacute;p bạn l&agrave;m phần c&ograve;n lại!</p>\r\n', 'vochuoi28-14609962101462253761.jpg', NULL, '0000-00-00', '0000-00-00', 13, 1),
(8, 'Canteen trường học khắp thế giới bán gì?', 'Bạn thường ăn gì cho bữa trưa tại trường? Có bao giờ bạn thắc mắc nhà ăn trường học ở nước ngoài bán gì không? Cùng chúng mình “đột nhập” canteen của các trường trên khắp thế giới nhé!', '<h2>&nbsp;</h2>\r\n\r\n<div>\r\n<p><strong>Mỹ</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: center;"><img alt="165-01-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-01-8eae7.jpg" style="border:0px; line-height:inherit" /></div>\r\n\r\n<div style="text-align: center;">Bữa trưa canteen của Mỹ tuy đầy đủ nhưng được đ&aacute;nh gi&aacute; l&agrave; kh&ocirc;ng tốt cho sức khỏe.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một suất ăn ti&ecirc;u chuẩn cho bữa trưa ở c&aacute;c trường học ở Mỹ thường bao gồm g&agrave; chi&ecirc;n gi&ograve;n, ăn k&egrave;m với sốt c&agrave; chua, đậu H&agrave; Lan, hoa quả đ&oacute;ng hộp v&agrave; một chiếc b&aacute;nh quy cỡ bự. Theo c&aacute;c chuy&ecirc;n gia dinh dưỡng, đ&acirc;y l&agrave; một suất ăn tương đối kh&ocirc;ng khoa học, khi thiếu chất xơ v&agrave; rau quả tươi. Vậy n&ecirc;n cũng dễ hiểu khi m&agrave; tỷ lệ b&eacute;o ph&igrave; ở Mỹ lu&ocirc;n v&agrave;o hạng cao nhất thế giới.</p>\r\n\r\n<p><strong>&Yacute;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: center;"><img alt="165-02-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-02-8eae7.jpg" style="border:0px; line-height:inherit" /></div>\r\n\r\n<div style="text-align: center;">Bữa trưa đẹp mắt v&agrave; đậm chất &Yacute; của canteen trường học &Yacute;.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute; bữa trưa ở nh&agrave; ăn trường học ở &Yacute; được nhận x&eacute;t l&agrave; c&acirc;n bằng, đầy đủ dinh dưỡng, v&agrave; c&ograve;n đẹp mắt nữa khi c&oacute; mỳ ống, b&aacute;nh mỳ, salad c&aacute; ngừ, c&agrave; chua trộn ph&ocirc; mai feta v&agrave; hoa quả theo m&ugrave;a.</p>\r\n\r\n<p><strong>Phần Lan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><img alt="165-03-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-03-8eae7.jpg" style="border:0px; line-height:inherit" /></div>\r\n\r\n<div>Bữa trưa trường học ở Phần Lan được đ&aacute;nh gi&aacute; cao bởi c&aacute;c chuy&ecirc;n gia dinh dưỡng.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một suất ăn ti&ecirc;u chuẩn cho bữa trưa trong trường học của Phần Lan l&agrave; s&uacute;p đậu H&agrave; Lan, c&agrave; rốt tươi, củ cải đỏ, một l&aacute;t b&aacute;nh mỳ tr&ograve;n, c&ugrave;ng pancake ăn k&egrave;m hoa quả theo m&ugrave;a. Nhiều nh&agrave; dinh dưỡng đ&aacute;nh gi&aacute; cao bữa trưa trường học của Phần Lan bởi c&oacute; nhiều chất xơ v&agrave; rau quả tươi trong khẩu phần.</p>\r\n\r\n<p><strong>H&agrave;n Quốc</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><img alt="165-04-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-04-8eae7.jpg" style="border:0px; line-height:inherit" /></div>\r\n\r\n<div>Bữa ăn trưa trong c&aacute;c trường ở H&agrave;n Quốc.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cơm chi&ecirc;n ăn c&ugrave;ng đậu phụ, canh c&aacute;, bắp cải muối, ớt chu&ocirc;ng trộn dấm v&agrave; s&uacute;p lơ xanh l&agrave; những g&igrave; m&agrave; canteen trường học ở H&agrave;n Quốc phục vụ học sinh, sinh vi&ecirc;n. C&aacute;c chuy&ecirc;n gia dinh dưỡng tr&ecirc;n to&agrave;n thế giới đều c&oacute; ấn tượng tốt với khẩu phần n&agrave;y.</p>\r\n\r\n<p><strong>Brazil</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><img alt="165-05-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-05-8eae7.jpg" style="border:0px; line-height:inherit" /></div>\r\n\r\n<div>Bạn c&oacute; nhận ra m&oacute;n g&igrave; ở g&oacute;c tr&ecirc;n b&ecirc;n phải trong bữa trưa đầy m&agrave;u sắc, k&iacute;ch th&iacute;ch vị gi&aacute;c n&agrave;y ở Brazil?</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Suất ăn trưa trong nh&agrave; ăn trường học ở Brazil g&acirc;y ấn tượng mạnh với c&aacute;ch tr&igrave;nh b&agrave;y ngon miệng, đầy m&agrave;u sắc. Đ&acirc;y đều l&agrave; những m&oacute;n ăn truyền thống của đất nước n&agrave;y, với cơm chi&ecirc;n đậu đen, b&aacute;nh mỳ nguy&ecirc;n hạt, ăn k&egrave;m chuối nướng, thịt lợn x&agrave;o ớt chu&ocirc;ng, c&agrave; chua v&agrave; rau trộn.</p>\r\n\r\n<p><strong>T&acirc;y Ban Nha</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><img alt="165-06-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-06-8eae7.jpg" style="border:0px; line-height:inherit" /></div>\r\n\r\n<div>Hải sản l&agrave; m&oacute;n ăn chủ đạo trong bữa trưa của đất nước nằm b&ecirc;n bờ biển n&agrave;y.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&aacute;nh mỳ nguy&ecirc;n hạt, cơm gạo lức ăn k&egrave;m t&ocirc;m chi&ecirc;n, s&uacute;p c&agrave; chua v&agrave; ớt chu&ocirc;ng trộn, c&ugrave;ng m&oacute;n tr&aacute;ng miệng một nửa tr&aacute;i cam l&agrave; những g&igrave; nh&agrave; ăn trường học T&acirc;y Ban Nha phục vụ.</p>\r\n\r\n<p><strong>Ph&aacute;p</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><strong><img alt="165-07-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-07-8eae7.jpg" style="border:0px; line-height:inherit" /></strong></div>\r\n\r\n<div>Bữa trưa trường học của Ph&aacute;p c&oacute; vẻ kh&aacute; đơn giản so với những quốc gia kh&aacute;c.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu học ở Ph&aacute;p, bạn sẽ c&oacute; cơ hội được ăn bữa trưa với rất nhiều chất xơ n&agrave;y ở trường. Một khẩu phần ăn trưa ti&ecirc;u chuẩn ở Ph&aacute;p bao gồm một phần b&iacute;t tết, ph&ocirc;mai Brie, c&agrave; rốt, đậu đũa, c&ugrave;ng t&aacute;o v&agrave; kiwi.</p>\r\n\r\n<p><strong>Hy Lạp</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div><strong><img alt="165-08-8eae7" src="http://k14.vcmedia.vn/thumb_w/600/3d082430da/2015/10/23/165-08-8eae7.jpg" style="border:0px; line-height:inherit" /></strong></div>\r\n\r\n<div>Suất ăn trường học ở Hy Lạp.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mỳ ống orzo (một loại mỳ ống đặc biệt, tr&ocirc;ng giống hạt gạo của Hy Lạp) ăn k&egrave;m thịt g&agrave; nướng, salad dưa chuột c&agrave; chua, sữa chua Hy Lạp, lựu v&agrave; qu&yacute;t l&agrave; những th&agrave;nh phần ti&ecirc;u chuẩn trong bữa trưa trong trường học ở Hy Lạp.</p>\r\n</div>\r\n', '165-03-8eae71462254580.jpg', NULL, '0000-00-00', '0000-00-00', 1, 0),
(9, '12 loại thực phẩm tốt cực tốt cho cún cưng của bạn', 'Với danh sách những loại thực phẩm đầy bổ dưỡng này, bạn sẽ không còn lo chú cún cưng của bạn bị ốm yếu đâu.', '<p>Trước giờ, nhiều người trong ch&uacute;ng ta thường lầm tưởng rằng, những ch&uacute; ch&oacute; - vật nu&ocirc;i phổ biến trong gia đ&igrave;nh vẫn sẽ khỏe mạnh nếu ăn tất-cả-mọi-thứ kể cả đồ ăn thừa. Thế nhưng sự thật lại ho&agrave;n to&agrave;n kh&ocirc;ng phải như vậy.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để nu&ocirc;i nấng một ch&uacute; ch&oacute; mạnh khỏe v&agrave; hoạt b&aacute;t, bạn phải cần c&oacute; những kiến thức cơ bản về chế độ thức ăn cho c&uacute;n cưng, cũng như cần nắm r&otilde; những loại&nbsp;<a href="http://kenh14.vn/thuc-pham.html" target="_blank" title="thực phẩm">thực phẩm</a>&nbsp;n&agrave;o n&ecirc;n tr&aacute;nh hoặc n&ecirc;n cho c&uacute;n ăn. Infographic dưới đ&acirc;y sẽ bật m&iacute; cho bạn biết thực đơn tốt cho những ch&uacute; c&uacute;n cưng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="12thucphamchocho2-e3226" src="http://k14.vcmedia.vn/thumb_w/600/0f8ddb7491/2015/10/22/12thucphamchocho2-e3226.jpg" /></p>\r\n\r\n<p style="text-align: center;">&nbsp;</p>\r\n\r\n<p style="text-align: right;">Nguồn: BusinessInsider</p>\r\n', 'group-of-pets1462254665.jpg', NULL, '0000-00-00', '0000-00-00', 13, 0),
(11, 'Bữa tiệc xa xỉ nhất lịch sử khiến hội nhà giàu cũng phải "GATO"', 'Bạn có tin, 250 chiếc Limousine được huy động để đưa đón khách, thực đơn toàn món hảo hạng và chi phí cho bữa tiệc xa hoa này có giá lên tới 11.000 tỷ đồng?', '<p>Bạn đ&atilde; từng nghe qua những&nbsp;<a href="http://kenh14.vn/bua-tiec.html" target="_blank" title="bữa tiệc">bữa tiệc</a>&nbsp;tốn h&agrave;ng triệu đ&ocirc; la của những người gi&agrave;u c&oacute; tr&ecirc;n thế giới, trong đ&oacute; kh&aacute;ch mời l&agrave; những nh&acirc;n vật nổi tiếng c&ograve;n c&aacute;c m&oacute;n ăn v&agrave; thức uống được phục vụ đều l&agrave;m từ những nguy&ecirc;n liệu hảo hạng. Ch&uacute;ng thật qu&aacute; xa xỉ so với đa số ch&uacute;ng ta hiện nay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, ngay cả những bữa tiệc xa hoa nhất của giới gi&agrave;u c&oacute; hiện nay vẫn chưa thấm v&agrave;o đ&acirc;u nếu so với bữa tiệc đ&atilde; được s&aacute;ch kỷ lục Guinness ghi nhận l&agrave; tốn k&eacute;m nhất trong&nbsp;<a href="http://kenh14.vn/lich-su.html" target="_blank" title="lịch sử">lịch sử</a>. Đ&oacute; l&agrave; Yến tiệc kỉ niệm 2.500 năm th&agrave;nh lập Đế quốc Ba Tư (do Cyrus Đại Đế s&aacute;ng lập) diễn ra v&agrave;o năm 1971. Ước t&iacute;nh,&nbsp;<strong>gi&aacute; trị của bữa tiệc n&agrave;y l&ecirc;n tới&nbsp;</strong><strong>516 triệu USD (hơn 10.836 tỷ đồng)</strong>...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xa xỉ từ trong th&agrave;nh phần tham dự...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Buổi Yến tiệc được tổ chức bởi vua Mohammad Reza Pahlavi (1919 - 1980) v&agrave;o ng&agrave;y 14/10/1971, nằm trong chuỗi c&aacute;c lễ hội nối tiếp nhau diễn ra từ ng&agrave;y 12 &ndash; 16/10 c&ugrave;ng năm.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c hoạt động n&agrave;y đều nhằm t&ocirc;n vinh lịch sử l&acirc;u d&agrave;i cũng như những th&agrave;nh tựu thời cận hiện đại của đất nước Iran v&agrave;o thời điểm đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec01-a3dd3.jpg" /><br />\r\nVua Mohammad Reza Pahlavi - chủ nh&acirc;n của bữa tiệc xa xỉ nhất trong lịch sử</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ cần nh&igrave;n qua danh s&aacute;ch kh&aacute;ch mời, ch&uacute;ng ta đ&atilde; c&oacute; thể thấy quy m&ocirc; ho&agrave;nh tr&aacute;ng của bữa tiệc n&agrave;y. C&oacute; lẽ, ngay tới cả những tỷ ph&uacute; gi&agrave;u nhất thế giới hiện nay cũng kh&oacute; mời được những l&atilde;nh đạo tối cao của nhiều quốc gia tới dự một bữa tiệc th&acirc;n mật như vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec02-a0913.jpg" /><br />\r\n<em>Kh&aacute;ch mời của bữa tiệc l&agrave; c&aacute;c vị nguy&ecirc;n thủ quốc gia tr&ecirc;n khắp thế giới</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; thể kể đến c&aacute;c nh&acirc;n vật nổi tiếng như Ho&agrave;ng tử Philip v&agrave; c&ocirc;ng ch&uacute;a Anne của Anh Quốc, Ch&iacute;nh kh&aacute;ch Nikolai Podgorny tới từ Ukraine, Đức Hồng Y Maximilien de Furstenberg của T&ograve;a th&aacute;nh Vatican... Ngo&agrave;i ra những nh&agrave; l&atilde;nh đạo cao cấp kh&aacute;c đến từ Đan Mạch, Bỉ, Ph&aacute;p, Mỹ, Malaysia, H&agrave;n Quốc, Ấn Độ&hellip; cũng tham dự bữa tiệc n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>... trong c&ocirc;ng t&aacute;c chuẩn bị đại tiệc...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bữa tiệc được tiến h&agrave;nh tại th&agrave;nh phố cổ Persepolis, nơi từng l&agrave; thủ đ&ocirc; của Đế chế Ba Tư cổ đại. Tại đ&acirc;y, ho&agrave;ng tộc Iran đ&atilde; thu&ecirc; một c&ocirc;ng ty thiết kế nội thất Maison Jansen của Paris để dựng c&aacute;c căn lều khổng lồ tr&ecirc;n diện t&iacute;ch 650.000m2. Tất cả sau đ&oacute; được sắp xếp th&agrave;nh h&igrave;nh ng&ocirc;i sao quanh một đ&agrave;i phun nước trung t&acirc;m.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec03a-0b83e.jpg" /><br />\r\n&nbsp;</p>\r\n\r\n<p>C&aacute;c lều nhỏ n&agrave;y l&agrave; nơi nghỉ ngơi của l&atilde;nh đạo c&aacute;c quốc gia. Mỗi lều đều c&oacute; đường d&acirc;y điện thoại v&agrave; fax tương ứng của quốc gia đại diện.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những lều lớn l&agrave; nơi tổ chức yến tiệc với k&iacute;ch thước l&agrave; 68 x 24m. Bao quanh c&aacute;c lều, vua Mohammad Reza Pahlavi đ&atilde; l&agrave;m một điều kh&ocirc;ng tưởng:&nbsp;<strong>phủ xanh diện t&iacute;ch đất sa mạc nơi diễn ra bữa tiệc bởi c&aacute;c loại c&acirc;y cối mang đến từ Ph&aacute;p.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec03-95676.jpg" /><br />\r\n<em>Đất sa mạc của khu vực xung quanh đ&atilde; được phủ xanh bằng c&aacute;c vườn c&acirc;y đem đến từ nước Ph&aacute;p</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để tạo th&ecirc;m độ xa xỉ cho đại tiệc, Ho&agrave;ng gia Iran đ&atilde; quyết định&nbsp;<strong>sử dụng trang phục được thiết kế bởi nh&agrave; thiết kế thời trang lừng danh Jeanne-Marie Lanvin</strong>.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Về vấn đề đi lại, c&oacute; tới&nbsp;<strong>250 chiếc Limousine mang thương hiệu Mercedes-Benz đ&atilde; được đặt h&agrave;ng để đưa đ&oacute;n kh&aacute;ch từ s&acirc;n bay tới địa điểm dự tiệc v&agrave; ngược lại.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><em><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec05-acfc8.jpg" /></em><br />\r\n<em>250 chiếc limousine như thế n&agrave;y đ&atilde; được sử dụng để đưa đ&oacute;n kh&aacute;ch mời dự tiệc</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với vấn đề ẩm thực,&nbsp;<strong>to&agrave;n bộ việc phục vụ đồ ăn sẽ được thực hiện bởi một trong những nh&agrave; h&agrave;ng nổi tiếng nhất của Ph&aacute;p</strong>&nbsp;- Maxim&#39;s de Paris. Thậm ch&iacute;, nh&agrave; h&agrave;ng n&agrave;y đ&atilde; phải đ&oacute;ng cửa trong hai tuần chỉ để chuẩn bị nh&acirc;n sự phục vụ cho bữa tiệc tại Iran.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, bộ đồ đựng thức ăn cũng được chế tạo bằng gốm sứ của h&atilde;ng Limoges, c&ograve;n những chiếc khăn ăn th&igrave; l&agrave;m từ lanh Porthault nổi danh tr&ecirc;n to&agrave;n cầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec07-6ce7e.jpg" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>... tới một thực đơn &quot;chỉ c&oacute; ở tr&ecirc;n giời&quot;...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng tất cả kh&ocirc;ng chỉ c&oacute; vậy, mức độ xa hoa của bữa tiệc n&agrave;y được thể hiện r&otilde; n&eacute;t nhất qua thực đơn khiến ngay cả những người gi&agrave;u c&oacute; nhất cũng chưa d&aacute;m nghĩ tới.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&agrave;o ng&agrave;y tổ chức buổi yến tiệc 14/10, 60 th&agrave;nh vi&ecirc;n ho&agrave;ng gia Ba Tư v&agrave; kh&aacute;ch mời tập trung trong căn lều lớn nhất v&agrave; thưởng thức c&aacute;c m&oacute;n mang đ&uacute;ng t&iacute;nh chất &quot;sơn h&agrave;o hải vị&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec09-16353.jpg" /><br />\r\n<em>Những loại rượu vang qu&yacute; hiếm bậc nhất thế giới...</em><br />\r\n&nbsp;</p>\r\n\r\n<p>Đ&oacute; l&agrave; trứng chim c&uacute;t bọc v&agrave;ng v&agrave; trứng c&aacute; tầm Caviar uống k&egrave;m rượu Champagne của v&ugrave;ng Ch&acirc;teau de Saran; kem l&agrave;m từ đu&ocirc;i t&ocirc;m sốt Nantua uống k&egrave;m rượu Ch&acirc;teau Haut-Brion Blanc 1964; thịt cừu nướng với nấm cục d&ugrave;ng c&ugrave;ng rượu vang Ch&acirc;teau Lafite Rothschild 1945; kem l&agrave;m từ rượu Champagne Mo&euml;t et Chandon 1911; Rượu Cognac Prince Eug&egrave;ne v&agrave; c&agrave; ph&ecirc; Mocha.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec08-16353.jpg" /></p>\r\n\r\n<p><em>... v&agrave; những m&oacute;n ăn c&oacute; lẽ kh&ocirc;ng thể đắt hơn đều được đem phục vụ tại bữa tiệc n&agrave;y</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đặc biệt, vua Mohammad Reza Pahlavi cũng hạ lệnh cho nướng 50 ch&uacute; c&ocirc;ng - vốn l&agrave; biểu tượng của Ba Tư, sau đ&oacute; nhồi với gan ngỗng ăn k&egrave;m chim c&uacute;t nướng hạnh nh&acirc;n v&agrave; salad nấm cục, uống với rượu vang Comte de Vogue 1945.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối bữa tiệc, kh&aacute;ch mời c&ograve;n được thưởng thức c&aacute;c tiết mục &quot;remix&quot; &acirc;m thanh, &aacute;nh s&aacute;ng kết hợp với nhạc điện tử do nh&agrave; soạn nhạc nổi tiếng Iannis Xenakis d&agrave;n dựng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&agrave; đ&acirc;y l&agrave; những con số tổng kết</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo t&iacute;nh to&aacute;n, bữa yến tiệc của ho&agrave;ng gia Iran đ&atilde; ti&ecirc;u tốn tổng cộng 25.000 chai rượu vang, 3.493 kg thịt, 3.629 kg bơ v&agrave; pho m&aacute;t, 473 l&iacute;t kem để phục vụ cho một số lượng khoảng 500 thực kh&aacute;ch.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: center;"><img alt="" src="http://k14.vcmedia.vn/thumb_w/600/6lnffOHA9gPXbn4CuEkXJZ6Pdcccc/Image/2015/04/150408kptiec06-6e256.jpg" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dĩ nhi&ecirc;n, tất cả trong số đ&oacute; đều l&agrave; những sản phẩm chất lượng hảo hạng nhất. Đ&aacute;ng kinh ngạc hơn, tất cả số sản phẩm ấy được sử dụng trong vỏn vẹn 5 tiếng rưỡi đồng hồ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>S&aacute;ch Kỷ lục Guinness tại thời điểm đ&oacute; v&agrave; c&aacute;c phi&ecirc;n bản sau n&agrave;y ghi nhận l&agrave; bữa tiệc xa hoa nhất trong lịch sử. V&agrave; c&aacute;i gi&aacute; cho n&oacute; th&igrave; v&ocirc; c&ugrave;ng khủng khiếp, với khoản tiền phải chi l&agrave; 90 triệu USD v&agrave;o năm 1971. Nếu t&iacute;nh theo gi&aacute; trị hiện tại, chi ph&iacute; n&agrave;y l&ecirc;n tới 516 triệu USD (tương đương hơn 10.836 tỷ đồng), một con số &quot;v&ocirc; tiền kho&aacute;ng hậu&quot; d&agrave;nh cho một yến tiệc trong lịch sử thế giới.</p>\r\n', '150408kptiec07-6ce7e1462254869.jpg', NULL, '0000-00-00', '0000-00-00', 13, 0),
(12, 'Hưởng thụ những lợi ích tuyệt vời khi ăn tôm', 'Bạn vẫn có thể yên tâm ăn tôm và hưởng thụ những lợi ích sức khỏe dưới đây nếu biết chọn tôm đúng cách!', '<p>T&ocirc;m l&agrave; thực phẩm quen thuộc trong c&aacute;c bữa ăn h&agrave;ng ng&agrave;y của mọi nh&agrave;. T&ocirc;m kh&ocirc;ng chứa tinh bột lại gi&agrave;u dinh dưỡng. Trong t&ocirc;m c&oacute; chứa nhiều protein, c&aacute;c vitamin A, B6, B12, E, B2, i ốt&hellip; v&agrave; c&aacute;c kho&aacute;ng chất như canxi, kali, magie, kẽm v&agrave; sắt n&ecirc;n ăn t&ocirc;m chắc chắn sẽ c&oacute; nhiều lợi &iacute;ch cho sức khỏe. Nếu bạn kh&ocirc;ng bị dị ứng với t&ocirc;m th&igrave; c&oacute; thể chọn thực phẩm n&agrave;y trong c&aacute;c bữa ăn. H&atilde;y c&ugrave;ng t&igrave;m hiểu về những lợi &iacute;ch cho sức khỏe khi bạn ăn t&ocirc;m nh&eacute;!</p>\r\n\r\n<p><strong>Tốt cho tim mạch</strong></p>\r\n\r\n<p><img alt="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 1." id="img_250873" src="http://k14.vcmedia.vn/2016/2-copy-1461989630587.jpg" title="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 1." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tốt cho xương</strong></p>\r\n\r\n<p><img alt="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 2." id="img_250874" src="http://k14.vcmedia.vn/2016/3-copy-1461989630907.jpg" title="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 2." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Gi&uacute;p giảm c&acirc;n</strong></p>\r\n\r\n<p><img alt="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 3." id="img_250878" src="http://k14.vcmedia.vn/2016/4-copy-1461989630311.jpg" title="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 3." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Gi&uacute;p ngăn rụng t&oacute;c</strong></p>\r\n\r\n<p><img alt="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 4." id="img_250879" src="http://k14.vcmedia.vn/2016/5-copy-1461989630442.jpg" title="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 4." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Gi&uacute;p ngăn ngừa ung thư</strong></p>\r\n\r\n<p><img alt="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 5." id="img_250880" src="http://k14.vcmedia.vn/2016/6-copy-1461989631178.jpg" title="Hưởng thụ những lợi ích tuyệt vời khi ăn tôm - Ảnh 5." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lưu &yacute;:</strong></p>\r\n\r\n<p>- Một số người chỉ ăn một phần của con t&ocirc;m nhưng bạn n&ecirc;n cố gắng ăn cả phần đu&ocirc;i sẽ tốt hơn.</p>\r\n\r\n<p>- Để tr&aacute;nh ăn phải t&ocirc;m độc, h&atilde;y học c&aacute;ch ph&acirc;n biệt t&ocirc;m tươi với t&ocirc;m bơm thuốc theo c&aacute;c bước đơn giản sau: T&ocirc;m c&oacute; bơm thuốc hoặc tạp chất thường to b&eacute;o mập mạp, thớ thịt căng, người cong bất thường, đu&ocirc;i x&ograve;e. Trong khi đ&oacute;, t&ocirc;m &quot;sạch&quot; đu&ocirc;i gọn g&agrave;ng, nhỏ, cụp, người thẳng hoặc cong nhẹ.</p>\r\n', 'ddd1462347842.jpg', NULL, '0000-00-00', '0000-00-00', 13, 0),
(13, '5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc', 'Một nguyên liệu, không đầy hai phút chuẩn bị và bạn sẽ có làn da mịn màng, không thấy lỗ chân lông nữa!', '<p>Lỗ ch&acirc;n l&ocirc;ng to l&agrave; một trong những vấn đề m&agrave; c&aacute;c bạn g&aacute;i da nhờn thường xuy&ecirc;n gặp phải. Đặc biệt v&agrave;o m&ugrave;a h&egrave;, vấn đề về lỗ ch&acirc;n l&ocirc;ng lại c&agrave;ng trở n&ecirc;n nghi&ecirc;m trọng hơn khi cơ thể tiết ra một lượng lớn mồ h&ocirc;i v&agrave; dầu nhờn. Điều n&agrave;y kh&ocirc;ng chỉ khiến da trở n&ecirc;n th&ocirc; r&aacute;p, sần s&ugrave;i, dễ sinh mụn m&agrave; c&ograve;n l&agrave; một trong những nguy&ecirc;n nh&acirc;n g&acirc;y l&atilde;o h&oacute;a da sớm. Để khắc phục t&igrave;nh trạng tr&ecirc;n, bạn ho&agrave;n to&agrave;n c&oacute; thể &aacute;p dụng một số loại mặt nạ đơn giản sau:</p>\r\n\r\n<p><strong>Mặt nạ khoai t&acirc;y</strong></p>\r\n\r\n<p style="text-align: center;"><img alt="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 1." id="img_251315" src="http://k14.vcmedia.vn/2016/1-1462017260095.jpg" title="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 1." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau khi gọt vỏ v&agrave; rửa sạch khoai t&acirc;y, bạn h&atilde;y cho ch&uacute;ng v&agrave;o m&aacute;y sinh tố xay nhuyễn rồi &eacute;p lấy nước. Massage nhẹ nh&agrave;ng da mặt với nước &eacute;p khoai t&acirc;y trong 15 ph&uacute;t trước khi rửa lại bằng nước ấm. Bạn n&ecirc;n thực hiện c&ocirc;ng thức n&agrave;y 2-3 lần/tuần để đạt được kết quả tốt nhất.</p>\r\n\r\n<p><strong>Mặt nạ c&agrave; chua</strong></p>\r\n\r\n<p><img alt="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 2." id="img_251316" src="http://k14.vcmedia.vn/2016/2-1462017260354.jpg" title="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 2." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đơn giản hơn mặt nạ khoai t&acirc;y, đối với c&agrave; chua, bạn c&oacute; thể cắt l&aacute;t mỏng v&agrave; đắp l&ecirc;n mặt. Trong l&uacute;c đắp mặt nạ, thưởng thức một cốc nước &eacute;p c&agrave; chua sẽ gi&uacute;p bạn c&oacute; l&agrave;n da s&aacute;ng hồng khỏe mạnh hơn. Bạn n&ecirc;n đắp mặt nạ c&agrave; chua 3 lần/tuần v&agrave;o buổi tối trước khi đi ngủ.</p>\r\n\r\n<p><strong>L&ograve;ng trắng trứng</strong></p>\r\n\r\n<p><img alt="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 3." id="img_251317" src="http://k14.vcmedia.vn/2016/3-1462017260155.jpg" title="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 3." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bạn h&atilde;y trộn đều l&ograve;ng trắng trứng v&agrave; 1 th&igrave;a mật ong. Sau đ&oacute;, d&ugrave;ng b&ocirc;ng tẩy trang thấm dung dịch v&agrave; thoa đều khắp mặt. Đợi đến khi mặt nạ kh&ocirc;, rửa sạch mặt với nước ấm. Nếu bạn thực hiện c&ocirc;ng thức n&agrave;y đều đặn 2 lần/tuần, chắc chắn bạn sẽ bất ngờ với sự thay đổi l&agrave;n da của m&igrave;nh.</p>\r\n\r\n<p><strong>Mặt nạ dưa chuột</strong></p>\r\n\r\n<p><img alt="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 4." id="img_251318" src="http://k14.vcmedia.vn/2016/4-1462017260284.jpg" title="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 4." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bạn n&ecirc;n để dưa chuột trong ngăn m&aacute;t tủ lạnh để ch&uacute;ng kh&ocirc;ng bị h&eacute;o, đồng thời tăng độ m&aacute;t khiến da được thư gi&atilde;n s&acirc;u hơn. Cắt dưa chuột th&agrave;nh l&aacute;t mỏng v&agrave; đắp l&ecirc;n mặt 20 ph&uacute;t mỗi ng&agrave;y để c&oacute; đủ độ ẩm cần thiết cũng như gi&uacute;p da thư gi&atilde;n sau 1 ng&agrave;y d&agrave;i mệt mỏi.</p>\r\n\r\n<p><strong>Mặt nạ đu đủ</strong></p>\r\n\r\n<p><img alt="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 5." id="img_251319" src="http://k14.vcmedia.vn/2016/5-1462017260213.jpg" title="5 loại mặt nạ giúp thu nhỏ lỗ chân lông ngay tức khắc - Ảnh 5." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bạn h&atilde;y cắt một miếng đu đủ vừa cầm tay rồi lấy l&ograve;ng trong miếng đu đủ m&aacute;t xa đều l&ecirc;n da mặt trong 10 &ndash; 15 ph&uacute;t. Bạn c&oacute; thể thay đu đủ bằng vỏ chuối, ch&uacute;ng sẽ nhanh ch&oacute;ng gi&uacute;p bạn sẽ kh&iacute;t lỗ ch&acirc;n l&ocirc;ng tr&ecirc;n da, ngo&agrave;i ra c&ograve;n l&agrave;m mờ c&aacute;c nhược điểm kh&aacute;c tr&ecirc;n gương mặt như vết th&acirc;m mụn, n&aacute;m, t&agrave;n nhang.</p>\r\n', 'matna1462480406.jpg', NULL, '0000-00-00', '0000-00-00', 17, 0),
(14, 'Bí kíp để có làn da đẹp, không mụn dù thường xuyên thức đêm', 'Hướng dẫn các bước chăm sóc da cho bạn thức khuya để luôn khỏe đẹp xuyên suốt mùa thi!', '<p><strong>Thức khuya v&agrave; những t&aacute;c hại l&ecirc;n da</strong></p>\r\n\r\n<p>Nếu bạn li&ecirc;n tục thức khuya v&agrave; ngủ nướng v&agrave;o s&aacute;ng h&ocirc;m sau, hiện tượng thường gặp nhất l&agrave; da t&aacute;i m&agrave;u, mắt sưng, lỗ ch&acirc;n l&ocirc;ng ng&agrave;y một to ra v&agrave; mọc mụn &ndash; đặc biệt l&agrave; c&aacute;c loại mụn ẩn li ti. Nguy&ecirc;n do l&agrave; bởi da bạn chỉ c&oacute; khoảng thời gian t&aacute;i tạo, nghỉ ngơi thoải m&aacute;i nhất l&agrave; từ 11h khuya đến 2, 3h s&aacute;ng h&ocirc;m sau. Thức khuya v&agrave; học tập, l&agrave;m việc l&uacute;c n&agrave;y sẽ khiến da stress nặng v&agrave; ng&agrave;y c&agrave;ng xấu.</p>\r\n\r\n<p style="text-align: center;"><img alt="Bí kíp để có làn da đẹp, không mụn dù thường xuyên thức đêm - Ảnh 1." id="img_248713" src="http://k14.vcmedia.vn/thumb_w/650/2016/e27995de-ae83-4eb0-9a0e-ceed3289be20-1461853582026.jpg" title="Bí kíp để có làn da đẹp, không mụn dù thường xuyên thức đêm - Ảnh 1." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu ti&ecirc;n, da bạn sẽ mất một lượng ẩm lớn v&agrave; k&eacute;m tươi s&aacute;ng, mịn m&agrave;ng. Khi da mất c&acirc;n bằng độ ẩm, việc tiết th&ecirc;m dầu, l&agrave;m lỗ ch&acirc;n l&ocirc;ng to ra rồi mọc mụn l&agrave; điều tiếp theo. Trong c&aacute;c lời khuy&ecirc;n về chăm s&oacute;c da, đi ngủ sớm v&igrave; thế lu&ocirc;n l&agrave; lưu &yacute; quan trọng. Thế nhưng, kh&ocirc;ng phải l&uacute;c n&agrave;o ch&uacute;ng ta cũng c&oacute; thể sắp xếp thời gian để ngủ trươc 11h. Quy tr&igrave;nh dưỡng da thiết kế d&agrave;nh ri&ecirc;ng cho l&agrave;n da thức khuya sau sẽ gi&uacute;p bạn giải quyết vấn đề đ&oacute;!</p>\r\n\r\n<p><strong>C&aacute;c bước dưỡng da cho những bạn &quot;c&uacute; đ&ecirc;m&quot;</strong></p>\r\n\r\n<p>Việc quan trọng nhất để gi&uacute;p da khỏe mạnh sau một đ&ecirc;m thức khuya l&agrave; cung cấp đủ nước v&agrave; thư gi&atilde;n cho da. Khi da đủ ẩm, giảm stress do kh&ocirc;ng được nghỉ ngơi, khả năng gặp c&aacute;c vấn đề kể tr&ecirc;n cũng giảm đi rất nhiều. H&atilde;y tham khảo 4 bước dưỡng da buổi tối sau để việc thức khuya kh&ocirc;ng c&ograve;n ảnh hưởng đến da qu&aacute; nhiều nữa:</p>\r\n\r\n<p><img alt="Bí kíp để có làn da đẹp, không mụn dù thường xuyên thức đêm - Ảnh 2." id="img_248714" src="http://k14.vcmedia.vn/thumb_w/650/2016/night-copy-1461853597415.jpg" title="Bí kíp để có làn da đẹp, không mụn dù thường xuyên thức đêm - Ảnh 2." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&aacute;c phương ph&aacute;p massage cho da:</strong></p>\r\n\r\n<p>Ngo&agrave;i những bước dưỡng da quen thuộc kể tr&ecirc;n, massage cho da l&agrave; c&aacute;ch chăm s&oacute;c lạ lẫm hơn nhưng thật ra lại rất phổ biến ở c&aacute;c nước ch&acirc;u &Aacute; kh&aacute;c như Nhật v&agrave; H&agrave;n. Bạn c&oacute; thể thử c&aacute;c phương ph&aacute;p massage sau t&ugrave;y v&agrave;o nhu cầu v&agrave; điều kiện của bản th&acirc;n nh&eacute;:&nbsp;<a href="http://kenh14.vn/suc-khoe-gioi-tinh/thu-gon-ngay-2cm-guong-mat-chi-nho-massage-bang-thia-20150820095525541.chn" target="_blank" title="Massage với thìa làm nhỏ mặt">Massage với th&igrave;a l&agrave;m nhỏ mặt</a>,&nbsp;<a href="http://kenh14.vn/suc-khoe-gioi-tinh/cach-massage-kieu-han-giup-mat-thon-gon-tuoi-tinh-cho-nguoi-ngu-nhieu-20160229114332556.chn" target="_blank" title="massage kiểu Hàn cho gương mặt rạng rỡ">massage kiểu H&agrave;n cho gương mặt rạng&nbsp;rỡ</a>&hellip;</p>\r\n', 'mta1462480470.jpg', NULL, '0000-00-00', '0000-00-00', 13, 0);
INSERT INTO `post` (`id_post`, `title`, `mini_content`, `content`, `image`, `author`, `time_post`, `time_edit`, `n_views`, `slide_show`) VALUES
(15, 'Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát', 'Người đang mụn, dễ bị mụn cần kiêng các món sau để làm mát cơ thể, tránh dầu nhờn khiến mụn nặng thêm.', '<p>Thời tiết n&oacute;ng bức l&agrave; nguy&ecirc;n nh&acirc;n khiến t&igrave;nh trạng da nhờn v&agrave; mụn b&ugrave;ng ph&aacute;t. Những thực phẩm y&ecirc;u th&iacute;ch hằng ng&agrave;y cũng c&oacute; thể l&agrave; thủ phạm ch&iacute;nh g&acirc;y ra những đốm mụn đ&aacute;ng gh&eacute;t. C&ugrave;ng loại ch&uacute;ng ra khỏi thực đơn để l&agrave;n da mịn m&agrave;ng trở lại nh&eacute;!</p>\r\n\r\n<p><strong>Sữa động vật</strong></p>\r\n\r\n<p><img alt="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 1." id="img_233333" src="http://k14.vcmedia.vn/2016/anh-1-1461086123659.jpg" title="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 1." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với những t&iacute;n đồ của sữa th&igrave; đ&acirc;y l&agrave; một tin kh&ocirc;ng vui ch&uacute;t n&agrave;o bởi sữa được coi l&agrave; thủ phạm ch&iacute;nh g&acirc;y ra mụn trứng c&aacute;. Trong sữa động vật c&oacute; chứa hormone l&agrave;m tăng chất nhầy, g&acirc;y dư thừa estrogen k&iacute;ch th&iacute;ch sự ph&aacute;t triển của mụn. Thay v&agrave;o đ&oacute;, bổ sung sữa đậu n&agrave;nh hay nước &eacute;p rau củ sẽ gi&uacute;p bạn cải thiện l&agrave;n da.</p>\r\n\r\n<p><strong>Đồ ăn cay</strong></p>\r\n\r\n<p><img alt="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 2." id="img_233315" src="http://k14.vcmedia.vn/2016/anh-2-1461086123851.jpg" title="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 2." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những thực phẩm cay n&oacute;ng l&agrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh g&acirc;y ra t&igrave;nh trạng mụn sinh s&ocirc;i. C&aacute;c gia vị cay như ớt, hạt ti&ecirc;u,..l&agrave;m giảm qu&aacute; tr&igrave;nh thải độc của cơ thể, ảnh hưởng đến l&agrave;n da v&agrave; k&iacute;ch th&iacute;ch mụn trứng c&aacute;. Đặc biệt khi da bị mụn, những đồ ăn cay n&oacute;ng cần liệt v&agrave;o danh s&aacute;ch kh&ocirc;ng sử dụng để tr&aacute;nh l&agrave;m gia tăng t&igrave;nh trạng n&agrave;y hơn.</p>\r\n\r\n<p><strong>M&igrave; t&ocirc;m</strong></p>\r\n\r\n<p><img alt="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 3." id="img_233316" src="http://k14.vcmedia.vn/2016/anh-3-1461086123781.jpg" title="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 3." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&igrave; ăn liền chứa c&aacute;c chất bảo quản v&agrave; chất b&eacute;o c&oacute; hại do chi&ecirc;n trong nhiệt độ cao. Trong loại đồ ăn chế biến sẵn n&agrave;y cũng chứa h&agrave;m lượng muối cao g&acirc;y t&iacute;ch nước tr&ecirc;n da v&agrave; gia tăng mụn. Một số c&aacute;ch ăn m&igrave; để kh&ocirc;ng nổi mụn như ăn k&egrave;m với rau xanh thực chất cũng kh&ocirc;ng l&agrave;m giảm khả năng g&acirc;y mụn trứng c&aacute; của ch&uacute;ng. C&aacute;ch duy nhất để khiến t&igrave;nh trạng da mặt kh&ocirc;ng tệ hơn đ&oacute; ch&iacute;nh l&agrave; loại bỏ m&oacute;n ăn n&agrave;y ra khỏi thực đơn.</p>\r\n\r\n<p><strong>Đồ ăn chi&ecirc;n r&aacute;n</strong></p>\r\n\r\n<p><img alt="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 4." id="img_233321" src="http://k14.vcmedia.vn/2016/anh-4-1461086123926.jpg" title="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 4." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những đồ ăn như g&agrave; r&aacute;n, khoai t&acirc;y chi&ecirc;n,..chứa nhiều chất b&eacute;o xấu trong dầu mỡ khiến t&igrave;nh trạng mụn b&ugrave;ng ph&aacute;t. Thực phẩm chứa nhiều dầu mỡ g&acirc;y b&iacute;t tắc tuyến b&atilde; nhờn v&agrave; nổi mụn tr&ecirc;n da. Đồ ăn chi&ecirc;n r&aacute;n cũng l&agrave;m tăng qu&aacute; tr&igrave;nh oxy h&oacute;a v&agrave; ph&aacute; vỡ c&aacute;c dưỡng chất t&aacute;i tạo l&agrave;n da. Ngo&agrave;i việc chăm s&oacute;c da bằng c&aacute;ch bổ sung dưỡng chất hay d&ugrave;ng mỹ phẩm th&igrave; bạn cũng n&ecirc;n hạn chế ăn những thực phẩm n&agrave;y nếu kh&ocirc;ng muốn mụn b&ugrave;ng ph&aacute;t nh&eacute;!</p>\r\n\r\n<p><strong>Đồ ngọt</strong></p>\r\n\r\n<p><img alt="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 5." id="img_233326" src="http://k14.vcmedia.vn/2016/anh-5-1461086123716.jpg" title="Đừng ăn 5 thực phẩm sau nếu không muốn mụn bùng phát - Ảnh 5." /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đồ ăn chứa nhiều đường l&agrave;m tăng sản xuất insulin g&acirc;y ra t&igrave;nh trạng dầu dư thừa tr&ecirc;n da. Khi đ&oacute;, c&aacute;c tế b&agrave;o chết cũng như vi khuẩn tr&ecirc;n da sẽ gia tăng, g&acirc;y b&iacute;t tắc lỗ ch&acirc;n l&ocirc;ng. Da nhờn cũng ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n khiến mụn trứng c&aacute; xuất hiện nhiều hơn. V&igrave; vậy, ch&uacute;ng ta n&ecirc;n hạn chế ăn đồ ngọt v&agrave; tăng cường bổ sung c&aacute;c loại nước &eacute;p v&agrave;o thực đơn gi&uacute;p l&agrave;m đẹp da.</p>\r\n', 'anh31462480547.jpg', NULL, '0000-00-00', '0000-00-00', 13, 0);

